// Create Order Panel Component (Clinic only)
const CreateOrderPanel = () => {
  const [prices, setPrices] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [patientName, setPatientName] = useState('');
  const [deadline, setDeadline] = useState('');
  const [notes, setNotes] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const fetchPrices = async () => {
      try {
        const res = await API.get('/prices');
        setPrices(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching prices:', err);
        setLoading(false);
      }
    };

    fetchPrices();
  }, []);

  const addItem = (price) => {
    const existingItem = selectedItems.find(item => item.priceId === price._id);

    if (existingItem) {
      setSelectedItems(
        selectedItems.map(item => 
          item.priceId === price._id 
            ? { ...item, quantity: item.quantity + 1 }
            : item
        )
      );
    } else {
      setSelectedItems([
        ...selectedItems,
        {
          priceId: price._id,
          productId: price.product._id,
          productName: price.product.name,
          price: price.price,
          quantity: 1,
          color: ''
        }
      ]);
    }
  };

  const removeItem = (priceId) => {
    setSelectedItems(selectedItems.filter(item => item.priceId !== priceId));
  };

  const updateQuantity = (priceId, quantity) => {
    if (quantity < 1) return;

    setSelectedItems(
      selectedItems.map(item => 
        item.priceId === priceId 
          ? { ...item, quantity }
          : item
      )
    );
  };

  const updateColor = (priceId, color) => {
    setSelectedItems(
      selectedItems.map(item => 
        item.priceId === priceId 
          ? { ...item, color }
          : item
      )
    );
  };

  const handleSubmit = async () => {
    // Validation
    if (selectedItems.length === 0) {
      setErrorMessage('Добавьте хотя бы один товар');
      return;
    }

    if (!patientName.trim()) {
      setErrorMessage('Введите ФИО пациента');
      return;
    }

    if (!deadline) {
      setErrorMessage('Укажите срок сдачи работы');
      return;
    }

    setIsSubmitting(true);
    setErrorMessage('');

    try {
      // Transform items
      const orderItems = selectedItems.map(item => ({
        productId: item.productId,
        quantity: item.quantity,
        color: item.color
      }));

      const response = await API.post('/orders', {
        patientName,
        deadline,
        items: orderItems,
        notes
      });

      // Reset form
      setSelectedItems([]);
      setPatientName('');
      setDeadline('');
      setNotes('');
      setSuccessMessage(`Заказ #${response.data.orderNumber} успешно создан`);

      // Clear success message after 5 seconds
      setTimeout(() => {
        setSuccessMessage('');
      }, 5000);
    } catch (err) {
      console.error('Error creating order:', err);
      setErrorMessage(err.response?.data?.message || 'Ошибка при создании заказа');
    } finally {
      setIsSubmitting(false);
    }
  };

  const filteredPrices = prices.filter(
    price => price.product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const totalAmount = selectedItems.reduce(
    (total, item) => total + (item.price * item.quantity), 
    0
  );

  const cardBg = useColorModeValue('white', 'gray.700');
  const borderColor = useColorModeValue('gray.200', 'gray.600');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Heading size="md" mb={6}>Создание заказа</Heading>

      {successMessage && (
        <Box mb={6} p={4} borderRadius="md" bg="green.100" color="green.800">
          {successMessage}
        </Box>
      )}

      {errorMessage && (
        <Box mb={6} p={4} borderRadius="md" bg="red.100" color="red.800">
          {errorMessage}
        </Box>
      )}

      <SimpleGrid columns={{ base: 1, xl: 2 }} spacing={6}>
        <Box>
          <Card bg={cardBg} boxShadow="sm" borderRadius="lg" mb={4}>
            <CardHeader pb={2}>
              <Heading size="sm">Данные заказа</Heading>
            </CardHeader>
            <CardBody>
              <VStack spacing={4} align="stretch">
                <FormControl isRequired>
                  <FormLabel>ФИО пациента</FormLabel>
                  <Input 
                    placeholder="Введите ФИО пациента" 
                    value={patientName} 
                    onChange={e => setPatientName(e.target.value)}
                  />
                </FormControl>

                <FormControl isRequired>
                  <FormLabel>Срок сдачи</FormLabel>
                  <Input 
                    type="date" 
                    value={deadline} 
                    onChange={e => setDeadline(e.target.value)}
                    min={new Date().toISOString().split('T')[0]}
                  />
                </FormControl>

                <FormControl>
                  <FormLabel>Примечания</FormLabel>
                  <Input 
                    placeholder="Дополнительная информация" 
                    value={notes} 
                    onChange={e => setNotes(e.target.value)}
                  />
                </FormControl>
              </VStack>
            </CardBody>
          </Card>

          <Card bg={cardBg} boxShadow="sm" borderRadius="lg" mb={4}>
            <CardHeader pb={2}>
              <Heading size="sm">Товары и услуги</Heading>
            </CardHeader>
            <CardBody>
              <InputGroup mb={4}>
                <InputLeftElement pointerEvents="none">
                  <Icon as={FiSearch} color="gray.400" />
                </InputLeftElement>
                <Input
                  placeholder="Поиск товара..."
                  value={searchTerm}
                  onChange={e => setSearchTerm(e.target.value)}
                />
              </InputGroup>

              <Box maxH="400px" overflowY="auto">
                <Table variant="simple" size="sm">
                  <Thead position="sticky" top={0} bg={cardBg} zIndex={1}>
                    <Tr>
                      <Th>Название</Th>
                      <Th isNumeric>Цена</Th>
                      <Th width="80px"></Th>
                    </Tr>
                  </Thead>
                  <Tbody>
                    {filteredPrices.map(price => (
                      <Tr key={price._id}>
                        <Td>{price.product.name}</Td>
                        <Td isNumeric>₽{price.price.toLocaleString()}</Td>
                        <Td>
                          <IconButton
                            icon={<FiPlus />}
                            aria-label="Добавить"
                            size="xs"
                            colorScheme="brand"
                            onClick={() => addItem(price)}
                          />
                        </Td>
                      </Tr>
                    ))}
                  </Tbody>
                </Table>
              </Box>
            </CardBody>
          </Card>
        </Box>

        <Box>
          <Card bg={cardBg} boxShadow="sm" borderRadius="lg" mb={4}>
            <CardHeader pb={2}>
              <Heading size="sm">Корзина</Heading>
            </CardHeader>
            <CardBody>
              {selectedItems.length > 0 ? (
                <>
                  <Table variant="simple" size="sm">
                    <Thead>
                      <Tr>
                        <Th>Название</Th>
                        <Th>Кол-во</Th>
                        <Th>Цвет</Th>
                        <Th isNumeric>Цена</Th>
                        <Th isNumeric>Сумма</Th>
                        <Th width="40px"></Th>
                      </Tr>
                    </Thead>
                    <Tbody>
                      {selectedItems.map(item => (
                        <Tr key={item.priceId}>
                          <Td>{item.productName}</Td>
                          <Td>
                            <HStack maxW="100px">
                              <IconButton
                                icon={<Icon as={FiTrash2} />}
                                aria-label="Уменьшить"
                                size="xs"
                                variant="ghost"
                                colorScheme="red"
                                onClick={() => updateQuantity(item.priceId, item.quantity - 1)}
                              />
                              <Text textAlign="center" w="30px">{item.quantity}</Text>
                              <IconButton
                                icon={<FiPlus />}
                                aria-label="Увеличить"
                                size="xs"
                                variant="ghost"
                                colorScheme="brand"
                                onClick={() => updateQuantity(item.priceId, item.quantity + 1)}
                              />
                            </HStack>
                          </Td>
                          <Td>
                            <Input 
                              placeholder="Цвет"
                              size="sm"
                              value={item.color || ''}
                              onChange={e => updateColor(item.priceId, e.target.value)}
                            />
                          </Td>
                          <Td isNumeric>₽{item.price.toLocaleString()}</Td>
                          <Td isNumeric>₽{(item.price * item.quantity).toLocaleString()}</Td>
                          <Td>
                            <IconButton
                              icon={<FiTrash2 />}
                              aria-label="Удалить"
                              size="xs"
                              colorScheme="red"
                              variant="ghost"
                              onClick={() => removeItem(item.priceId)}
                            />
                          </Td>
                        </Tr>
                      ))}
                    </Tbody>
                  </Table>

                  <Divider my={4} />

                  <Flex justify="space-between" align="center">
                    <Text fontWeight="bold">Итого:</Text>
                    <Text fontWeight="bold" fontSize="xl">₽{totalAmount.toLocaleString()}</Text>
                  </Flex>

                  <Button 
                    colorScheme="brand" 
                    mt={4} 
                    width="full"
                    disabled={selectedItems.length === 0 || isSubmitting}
                    isLoading={isSubmitting}
                    onClick={handleSubmit}
                  >
                    Оформить заказ
                  </Button>
                </>
              ) : (
                <Center py={8}>
                  <VStack>
                    <Icon as={FiShoppingCart} fontSize="4xl" color="gray.400" />
                    <Text color="gray.500">Корзина пуста</Text>
                    <Text fontSize="sm" color="gray.400">
                      Добавьте товары из списка слева
                    </Text>
                  </VStack>
                </Center>
              )}
            </CardBody>
          </Card>
        </Box>
      </SimpleGrid>
    </Box>
  );
};

// Stat Card Component
const StatCard = ({ title, value, icon, accentColor }) => {
  const cardBg = useColorModeValue('white', 'gray.700');

  return (
    <Card bg={cardBg} boxShadow="sm" borderRadius="lg">
      <CardBody>
        <Flex justify="space-between">
          <Box>
            <Stat>
              <StatLabel fontSize="sm" fontWeight="medium" isTruncated>
                {title}
              </StatLabel>
              <StatNumber fontSize="3xl" fontWeight="bold">
                {value}
              </StatNumber>
            </Stat>
          </Box>
          <Flex
            flexShrink={0}
            alignItems="center"
            justifyContent="center"
            borderRadius="full"
            bg={`${accentColor}10`}
            color={accentColor}
            boxSize={12}
          >
            <Icon as={icon} boxSize={6} />
          </Flex>
        </Flex>
      </CardBody>
    </Card>
  );
};

// Helper functions
const getStatusText = (status) => {
  switch (status) {
    case 'pending': return 'Не оплачен';
    case 'paid': return 'Оплачен';
    case 'cancelled': return 'Отменен';
    default: return status;
  }
};

const getStatusColorScheme = (status) => {
  switch (status) {
    case 'pending': return 'orange';
    case 'paid': return 'green';
    case 'cancelled': return 'red';
    default: return 'gray';
  }
};

const getPluralForm = (count) => {
  // Russian plural forms for "item"
  if (count % 10 === 1 && count % 100 !== 11) {
    return 'товар';
  } else if ([2, 3, 4].includes(count % 10) && ![12, 13, 14].includes(count % 100)) {
    return 'товара';
  } else {
    return 'товаров';
  }
};

export default Dashboard;
