import React, { useEffect, useState } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Box,
  Heading,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  Input,
  InputGroup,
  InputLeftElement,
  Icon,
  Card,
  useColorModeValue,
  Center,
  Spinner,
  Button,
  Flex,
  Select,
  Badge,
  HStack,
  IconButton,
  Container
} from '@chakra-ui/react';
import { FiSearch, FiPlus, FiCornerDownRight } from 'react-icons/fi';
import API from '../utils/api';

// Helper functions
const getStatusText = (status) => {
  switch (status) {
    case 'pending': return 'Не оплачен';
    case 'paid': return 'Оплачен';
    case 'cancelled': return 'Отменен';
    default: return status;
  }
};

const getStatusColorScheme = (status) => {
  switch (status) {
    case 'pending': return 'orange';
    case 'paid': return 'green';
    case 'cancelled': return 'red';
    default: return 'gray';
  }
};

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await API.get('/orders');
        setOrders(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching orders:', err);
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  const filteredOrders = orders
    .filter(order => statusFilter ? order.status === statusFilter : true)
    .filter(order => 
      order.orderNumber.toString().includes(searchTerm)
    );

  const cardBg = useColorModeValue('white', 'gray.700');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Container maxW="container.lg" py={6}>
      <Flex justify="space-between" align="center" mb={4}>
        <Heading size="lg">Управление заказами</Heading>
        <Button 
          leftIcon={<FiPlus />} 
          colorScheme="brand"
          as={RouterLink}
          to="/create-order"
        >
          Создать заказ
        </Button>
      </Flex>

      <Flex mb={4} gap={4} direction={{ base: "column", md: "row" }}>
        <InputGroup>
          <InputLeftElement pointerEvents="none">
            <Icon as={FiSearch} color="gray.400" />
          </InputLeftElement>
          <Input
            placeholder="Поиск заказа..."
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value)}
          />
        </InputGroup>

        <Select 
          placeholder="Все статусы" 
          value={statusFilter} 
          onChange={e => setStatusFilter(e.target.value)}
          maxWidth={{ base: "100%", md: "200px" }}
        >
          <option value="pending">Не оплачен</option>
          <option value="paid">Оплачен</option>
          <option value="cancelled">Отменен</option>
        </Select>
      </Flex>

      <Card bg={cardBg} boxShadow="sm" borderRadius="lg" overflow="hidden">
        <Table variant="simple">
          <Thead>
            <Tr>
              <Th>№ заказа</Th>
              <Th>Дата</Th>
              <Th>Сумма</Th>
              <Th>Статус</Th>
              <Th width="120px">Действия</Th>
            </Tr>
          </Thead>
          <Tbody>
            {filteredOrders.map(order => (
              <Tr key={order._id}>
                <Td fontWeight="medium">#{order.orderNumber}</Td>
                <Td>{new Date(order.createdAt).toLocaleDateString()}</Td>
                <Td>₽{order.totalAmount.toLocaleString()}</Td>
                <Td>
                  <Badge colorScheme={getStatusColorScheme(order.status)}>
                    {getStatusText(order.status)}
                  </Badge>
                </Td>
                <Td>
                  <HStack spacing={2}>
                    <IconButton
                      icon={<FiCornerDownRight />}
                      aria-label="Детали"
                      size="sm"
                      colorScheme="blue"
                      variant="ghost"
                    />
                  </HStack>
                </Td>
              </Tr>
            ))}
          </Tbody>
        </Table>
      </Card>
    </Container>
  );
};

export default Orders;
