// Create Order Panel Component (Clinic only)
const CreateOrderPanel = () => {
  const [prices, setPrices] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPrices = async () => {
      try {
        const res = await API.get('/prices');
        setPrices(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching prices:', err);
        setLoading(false);
      }
    };

    fetchPrices();
  }, []);

  const addItem = (price) => {
    const existingItem = selectedItems.find(item => item.priceId === price._id);

    if (existingItem) {
      setSelectedItems(
        selectedItems.map(item => 
          item.priceId === price._id 
            ? { ...item, quantity: item.quantity + 1 }
            : item
        )
      );
    } else {
      setSelectedItems([
        ...selectedItems,
        {
          priceId: price._id,
          productId: price.product._id,
          productName: price.product.name,
          price: price.price,
          quantity: 1
        }
      ]);
    }
  };

  const removeItem = (priceId) => {
    setSelectedItems(selectedItems.filter(item => item.priceId !== priceId));
  };

  const updateQuantity = (priceId, quantity) => {
    if (quantity < 1) return;

    setSelectedItems(
      selectedItems.map(item => 
        item.priceId === priceId 
          ? { ...item, quantity }
          : item
      )
    );
  };

  const filteredPrices = prices.filter(
    price => price.product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const totalAmount = selectedItems.reduce(
    (total, item) => total + (item.price * item.quantity), 
    0
  );

  const cardBg = useColorModeValue('white', 'gray.700');
  const borderColor = useColorModeValue('gray.200', 'gray.600');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Heading size="md" mb={6}>Создание заказа</Heading>

      <SimpleGrid columns={{ base: 1, xl: 2 }} spacing={6}>
        <Box>
          <Card bg={cardBg} boxShadow="sm" borderRadius="lg" mb={4}>
            <CardHeader pb={2}>
              <Heading size="sm">Товары и услуги</Heading>
            </CardHeader>
            <CardBody>
              <InputGroup mb={4}>
                <InputLeftElement pointerEvents="none">
                  <Icon as={FiSearch} color="gray.400" />
                </InputLeftElement>
                <Input
                  placeholder="Поиск товара..."
                  value={searchTerm}
                  onChange={e => setSearchTerm(e.target.value)}
                />
              </InputGroup>

              <Box maxH="400px" overflowY="auto">
                <Table variant="simple" size="sm">
                  <Thead position="sticky" top={0} bg={cardBg} zIndex={1}>
                    <Tr>
                      <Th>Название</Th>
                      <Th isNumeric>Цена</Th>
                      <Th width="80px"></Th>
                    </Tr>
                  </Thead>
                  <Tbody>
                    {filteredPrices.map(price => (
                      <Tr key={price._id}>
                        <Td>{price.product.name}</Td>
                        <Td isNumeric>₽{price.price.toLocaleString()}</Td>
                        <Td>
                          <IconButton
                            icon={<FiPlus />}
                            aria-label="Добавить"
                            size="xs"
                            colorScheme="brand"
                            onClick={() => addItem(price)}
                          />
                        </Td>
                      </Tr>
                    ))}
                  </Tbody>
                </Table>
              </Box>
            </CardBody>
          </Card>
        </Box>

        <Box>
          <Card bg={cardBg} boxShadow="sm" borderRadius="lg" mb={4}>
            <CardHeader pb={2}>
              <Heading size="sm">Корзина</Heading>
            </CardHeader>
            <CardBody>
              {selectedItems.length > 0 ? (
                <>
                  <Table variant="simple" size="sm">
                    <Thead>
                      <Tr>
                        <Th>Название</Th>
                        <Th>Кол-во</Th>
                        <Th isNumeric>Цена</Th>
                        <Th isNumeric>Сумма</Th>
                        <Th width="40px"></Th>
                      </Tr>
                    </Thead>
                    <Tbody>
                      {selectedItems.map(item => (
                        <Tr key={item.priceId}>
                          <Td>{item.productName}</Td>
                          <Td>
                            <HStack maxW="100px">
                              <IconButton
                                icon={<Icon as={FiTrash2} />}
                                aria-label="Уменьшить"
                                size="xs"
                                variant="ghost"
                                colorScheme="red"
                                onClick={() => updateQuantity(item.priceId, item.quantity - 1)}
                              />
                              <Text textAlign="center" w="30px">{item.quantity}</Text>
                              <IconButton
                                icon={<FiPlus />}
                                aria-label="Увеличить"
                                size="xs"
                                variant="ghost"
                                colorScheme="brand"
                                onClick={() => updateQuantity(item.priceId, item.quantity + 1)}
                              />
                            </HStack>
                          </Td>
                          <Td isNumeric>₽{item.price.toLocaleString()}</Td>
                          <Td isNumeric>₽{(item.price * item.quantity).toLocaleString()}</Td>
                          <Td>
                            <IconButton
                              icon={<FiTrash2 />}
                              aria-label="Удалить"
                              size="xs"
                              colorScheme="red"
                              variant="ghost"
                              onClick={() => removeItem(item.priceId)}
                            />
                          </Td>
                        </Tr>
                      ))}
                    </Tbody>
                  </Table>

                  <Divider my={4} />

                  <Flex justify="space-between" align="center">
                    <Text fontWeight="bold">Итого:</Text>
                    <Text fontWeight="bold" fontSize="xl">₽{totalAmount.toLocaleString()}</Text>
                  </Flex>

                  <Button 
                    colorScheme="brand" 
                    mt={4} 
                    width="full"
                    disabled={selectedItems.length === 0}
                  >
                    Оформить заказ
                  </Button>
                </>
              ) : (
                <Center py={8}>
                  <VStack>
                    <Icon as={FiShoppingCart} fontSize="4xl" color="gray.400" />
                    <Text color="gray.500">Корзина пуста</Text>
                    <Text fontSize="sm" color="gray.400">
                      Добавьте товары из списка слева
                    </Text>
                  </VStack>
                </Center>
              )}
            </CardBody>
          </Card>
        </Box>
      </SimpleGrid>
    </Box>
  );
};

