import React, { useEffect, useState, useContext } from 'react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import {
  Box,
  Heading,
  Text,
  SimpleGrid,
  Stat,
  StatLabel,
  StatNumber,
  Card,
  CardHeader,
  CardBody,
  Button,
  Flex,
  Icon,
  Divider,
  useColorModeValue,
  Spinner,
  Center,
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
  HStack,
  VStack,
  Avatar,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  Input,
  Select,
  Badge,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  FormControl,
  FormLabel,
  IconButton,
  InputGroup,
  InputLeftElement,
  Textarea,
  AlertDialog,
  AlertDialogOverlay,
  AlertDialogContent,
  AlertDialogHeader,
  AlertDialogBody,
  AlertDialogFooter,
  useToast,
  Stack,
  FormHelperText,
} from '@chakra-ui/react';
import { FiShoppingCart, FiDollarSign, FiAlertCircle, FiUser, FiPackage, FiLogOut, FiSearch, FiPlus, FiEdit, FiTrash2, FiCornerDownRight, FiCheck } from 'react-icons/fi';
import API from '../utils/api';
import AuthContext from '../context/AuthContext';

const Dashboard = () => {
  const { user, isAdmin, logout } = useContext(AuthContext);
  const [activeTab, setActiveTab] = useState(0);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const cardBg = useColorModeValue('white', 'gray.700');
  const borderColor = useColorModeValue('gray.200', 'gray.700');

  // Load data based on active tab
  useEffect(() => {
    setLoading(false);
  }, [activeTab]);

  return (
    <Box>
      {/* Header */}
      <Flex 
        bg={cardBg} 
        p={4} 
        shadow="sm" 
        borderBottomWidth="1px" 
        borderColor={borderColor}
        justify="space-between"
        align="center"
        position="sticky"
        top={0}
        zIndex={10}
      >
        <Heading size="lg">Clinic Price Manager</Heading>

        <HStack spacing={4}>
          <Text>{user?.username}</Text>
          <Menu>
            <MenuButton 
              as={Avatar}
              size="sm"
              bg="brand.500"
              color="white"
              cursor="pointer"
            />
            <MenuList>
              <MenuItem 
                icon={<Icon as={FiLogOut} />} 
                onClick={handleLogout}
              >
                Выйти
              </MenuItem>
            </MenuList>
          </Menu>
        </HStack>
      </Flex>

      <Box p={4}>
        <Tabs 
          colorScheme="brand" 
          variant="enclosed" 
          onChange={(index) => setActiveTab(index)}
          isLazy
        >
          <TabList mb={4}>
            <Tab><Icon as={FiUser} mr={2} />Панель управления</Tab>

            {isAdmin ? (
              <>
                <Tab><Icon as={FiUser} mr={2} />Клиники</Tab>
                <Tab><Icon as={FiPackage} mr={2} />Товары</Tab>
                <Tab><Icon as={FiShoppingCart} mr={2} />Заказы</Tab>
              </>
            ) : (
              <>
                <Tab><Icon as={FiPackage} mr={2} />Прайс-лист</Tab>
                <Tab><Icon as={FiShoppingCart} mr={2} />Заказы</Tab>
                <Tab><Icon as={FiPlus} mr={2} />Создать заказ</Tab>
              </>
            )}
          </TabList>

          <TabPanels>
            {/* Dashboard Panel */}
            <TabPanel p={0}>
              <DashboardPanel isAdmin={isAdmin} />
            </TabPanel>

            {/* Admin Panels */}
            {isAdmin ? (
              <>
                <TabPanel p={0}>
                  <ClinicsPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <ProductsPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <OrdersPanel isAdmin={true} />
                </TabPanel>
              </>
            ) : (
              <>
                {/* Clinic Panels */}
                <TabPanel p={0}>
                  <PriceListPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <OrdersPanel isAdmin={false} />
                </TabPanel>
                <TabPanel p={0}>
                  <CreateOrderPanel />
                </TabPanel>
              </>
            )}
          </TabPanels>
        </Tabs>
      </Box>
    </Box>
  );
};

// Dashboard Panel Component
const DashboardPanel = ({ isAdmin }) => {
  const [stats, setStats] = useState({
    totalOrders: 0,
    pendingOrders: 0,
    totalSpent: 0,
    recentOrders: [],
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        // Get all orders
        const ordersRes = await API.get('/orders');

        // Get pending orders
        const pendingRes = await API.get('/orders/unpaid');

        // Calculate total spent (for paid orders only)
        const totalSpent = ordersRes.data
          .filter(order => order.status === 'paid')
          .reduce((acc, order) => acc + order.totalAmount, 0);

        setStats({
          totalOrders: ordersRes.data.length,
          pendingOrders: pendingRes.data.length,
          totalSpent,
          recentOrders: ordersRes.data.slice(0, 5), // Get 5 most recent orders
        });

        setLoading(false);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const cardBg = useColorModeValue('white', 'gray.700');
  const textColor = useColorModeValue('gray.600', 'gray.200');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Heading size="md" mb={6}>
        {isAdmin ? 'Статистика системы' : 'Статистика клиники'}
      </Heading>

      <SimpleGrid columns={{ base: 1, md: 3 }} spacing={6} mb={6}>
        <StatCard
          title="Всего заказов"
          value={stats.totalOrders}
          icon={FiShoppingCart}
          accentColor="brand.500"
        />
        <StatCard
          title="Неоплаченные заказы"
          value={stats.pendingOrders}
          icon={FiAlertCircle}
          accentColor="orange.500"
        />
        <StatCard
          title="Сумма оплаченных заказов"
          value={`₽${stats.totalSpent.toLocaleString()}`}
          icon={FiDollarSign}
          accentColor="green.500"
        />
      </SimpleGrid>

      <Card bg={cardBg} boxShadow="sm" borderRadius="lg">
        <CardHeader>
          <Heading size="md">Последние заказы</Heading>
        </CardHeader>
        <CardBody>
          {stats.recentOrders.length > 0 ? (
            stats.recentOrders.map((order, index) => (
              <React.Fragment key={order._id}>
                <Flex justify="space-between" align="center" py={2}>
                  <Box>
                    <Text fontWeight="bold">Заказ #{order.orderNumber}</Text>
                    <Text fontSize="sm" color={textColor}>
                      {new Date(order.createdAt).toLocaleDateString()} • {order.items.length} {getPluralForm(order.items.length)}
                    </Text>
                  </Box>
                  <Box>
                    <Badge colorScheme={getStatusColorScheme(order.status)}>
                      {getStatusText(order.status)}
                    </Badge>
                    <Text fontWeight="medium" fontSize="md">
                      ₽{order.totalAmount.toLocaleString()}
                    </Text>
                  </Box>
                </Flex>
                {index < stats.recentOrders.length - 1 && <Divider />}
              </React.Fragment>
            ))
          ) : (
            <Text>Нет заказов</Text>
          )}
        </CardBody>
      </Card>
    </Box>
  );
};

// Clinics Panel Component (Admin only)
const ClinicsPanel = () => {
  const [clinics, setClinics] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [isDeleteDialogOpen, setIsDeleteDialogOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedClinic, setSelectedClinic] = useState(null);
  const [newClinic, setNewClinic] = useState({ 
    name: '', 
    address: '',
    contact: { phone: '', email: '' },
    active: true,
    username: '',
    password: ''
  });
  const [editedClinic, setEditedClinic] = useState(null);
  const [submitLoading, setSubmitLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const toast = useToast();

  useEffect(() => {
    fetchClinics();
  }, []);

  const fetchClinics = async () => {
    try {
      setLoading(true);
      const res = await API.get('/clinics');
      setClinics(res.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching clinics:', err);
      toast({
        title: 'Ошибка загрузки клиник',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
      setLoading(false);
    }
  };

  const handleAddClinic = async () => {
    try {
      setSubmitLoading(true);
      const { username, password, ...clinicData } = newClinic;
      await API.post('/clinics', { 
        ...clinicData, 
        credentials: { username, password } 
      });
      setIsAddModalOpen(false);
      setNewClinic({ 
        name: '', 
        address: '',
        contact: { phone: '', email: '' },
        active: true,
        username: '',
        password: ''
      });
      fetchClinics();
      toast({
        title: 'Клиника успешно добавлена',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (err) {
      console.error('Error adding clinic:', err);
      toast({
        title: 'Ошибка при добавлении клиники',
        description: err.response?.data?.message || 'Что-то пошло не так',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setSubmitLoading(false);
    }
  };

  const handleEditClinic = async () => {
    try {
      setSubmitLoading(true);
      const { _id, createdAt, updatedAt, __v, ...updateData } = editedClinic;

      // If password is empty, don't send it in the update
      if (updateData.password === '') {
        delete updateData.password;
      }

      await API.put(`/clinics/${_id}`, updateData);
      setIsEditModalOpen(false);
      setEditedClinic(null);
      fetchClinics();
      toast({
        title: 'Клиника успешно обновлена',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (err) {
      console.error('Error updating clinic:', err);
      toast({
        title: 'Ошибка при обновлении клиники',
        description: err.response?.data?.message || 'Что-то пошло не так',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setSubmitLoading(false);
    }
  };

  const handleDeleteClinic = async () => {
    try {
      setSubmitLoading(true);
      await API.delete(`/clinics/${selectedClinic._id}`);
      setIsDeleteDialogOpen(false);
      setSelectedClinic(null);
      fetchClinics();
      toast({
        title: 'Клиника успешно удалена',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (err) {
      console.error('Error deleting clinic:', err);
      toast({
        title: 'Ошибка при удалении клиники',
        description: err.response?.data?.message || 'Что-то пошло не так',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setSubmitLoading(false);
    }
  };

  const filteredClinics = clinics.filter(
    clinic => clinic.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const cardBg = useColorModeValue('white', 'gray.700');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Flex justify="space-between" align="center" mb={4}>
        <Heading size="md">Управление клиниками</Heading>
