                      {clinic.active ? 'Активна' : 'Неактивна'}
                    </Badge>
                  </Td>
                  <Td>
                    <HStack spacing={2}>
                      <IconButton
                        icon={<FiEdit />}
                        aria-label="Редактировать"
                        size="sm"
                        colorScheme="blue"
                        variant="ghost"
                        onClick={() => {
                          // Make a copy for editing
                          const clinicForEdit = { ...clinic };
                          // Initialize with empty password field for editing
                          clinicForEdit.password = '';
                          setEditedClinic(clinicForEdit);
                          setIsEditModalOpen(true);
                        }}
                      />
                      <IconButton
                        icon={<FiTrash2 />}
                        aria-label="Удалить"
