                    />
                    <InputRightElement width="4.5rem">
                      <Button 
                        h="1.75rem" 
                        size="sm" 
                        onClick={() => setShowPassword(!showPassword)}
                      >
                        {showPassword ? "Скрыть" : "Показать"}
                      </Button>
                    </InputRightElement>
                  </InputGroup>
                </FormControl>
              </SimpleGrid>
            </VStack>
          </ModalBody>
          <ModalFooter>
            <Button variant="outline" mr={3} onClick={() => setIsAddModalOpen(false)}>
              Отмена
            </Button>
            <Button 
              colorScheme="brand" 
              onClick={handleAddClinic}
              isLoading={submitLoading}
              isDisabled={!newClinic.name.trim() || !newClinic.username.trim() || !newClinic.password.trim()}
            >
              Создать
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>

      {/* Edit Clinic Modal */}
      <Modal isOpen={isEditModalOpen} onClose={() => setIsEditModalOpen(false)} size="lg">
