import React, { useEffect, useState, useContext } from 'react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import {
  Box,
  Heading,
  Text,
  SimpleGrid,
  Stat,
  StatLabel,
  StatNumber,
  Card,
  CardHeader,
  CardBody,
  Button,
  Flex,
  Icon,
  Divider,
  useColorModeValue,
  Spinner,
  Center,
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
  HStack,
  VStack,
  Avatar,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  Input,
  Select,
  Badge,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  FormControl,
  FormLabel,
  IconButton,
  InputGroup,
  InputLeftElement,
} from '@chakra-ui/react';
import { FiShoppingCart, FiDollarSign, FiAlertCircle, FiUser, FiPackage, FiLogOut, FiSearch, FiPlus, FiEdit, FiTrash2, FiCornerDownRight, FiCheck } from 'react-icons/fi';
import API from '../utils/api';
import AuthContext from '../context/AuthContext';

const Dashboard = () => {
  const { user, isAdmin, logout } = useContext(AuthContext);
  const [activeTab, setActiveTab] = useState(0);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const cardBg = useColorModeValue('white', 'gray.700');
  const borderColor = useColorModeValue('gray.200', 'gray.700');

  // Load data based on active tab
  useEffect(() => {
    setLoading(false);
  }, [activeTab]);

  return (
    <Box>
      {/* Header */}
      <Flex 
        bg={cardBg} 
        p={4} 
        shadow="sm" 
        borderBottomWidth="1px" 
        borderColor={borderColor}
        justify="space-between"
        align="center"
        position="sticky"
        top={0}
        zIndex={10}
      >
        <Heading size="lg">Clinic Price Manager</Heading>

        <HStack spacing={4}>
          <Text>{user?.username}</Text>
          <Menu>
            <MenuButton 
              as={Avatar}
              size="sm"
              bg="brand.500"
              color="white"
              cursor="pointer"
            />
            <MenuList>
              <MenuItem 
                icon={<Icon as={FiLogOut} />} 
                onClick={handleLogout}
              >
                Выйти
              </MenuItem>
            </MenuList>
          </Menu>
        </HStack>
      </Flex>

      <Box p={4}>
        <Tabs 
          colorScheme="brand" 
          variant="enclosed" 
          onChange={(index) => setActiveTab(index)}
          isLazy
        >
          <TabList mb={4}>
            <Tab><Icon as={FiUser} mr={2} />Панель управления</Tab>

            {isAdmin ? (
              <>
                <Tab><Icon as={FiUser} mr={2} />Клиники</Tab>
                <Tab><Icon as={FiPackage} mr={2} />Товары</Tab>
                <Tab><Icon as={FiShoppingCart} mr={2} />Заказы</Tab>
              </>
            ) : (
              <>
                <Tab><Icon as={FiPackage} mr={2} />Прайс-лист</Tab>
                <Tab><Icon as={FiShoppingCart} mr={2} />Заказы</Tab>
                <Tab><Icon as={FiPlus} mr={2} />Создать заказ</Tab>
              </>
            )}
          </TabList>

          <TabPanels>
            {/* Dashboard Panel */}
            <TabPanel p={0}>
              <DashboardPanel isAdmin={isAdmin} />
            </TabPanel>

            {/* Admin Panels */}
            {isAdmin ? (
              <>
                <TabPanel p={0}>
                  <ClinicsPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <ProductsPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <OrdersPanel isAdmin={true} />
                </TabPanel>
              </>
            ) : (
              <>
                {/* Clinic Panels */}
                <TabPanel p={0}>
                  <PriceListPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <OrdersPanel isAdmin={false} />
                </TabPanel>
                <TabPanel p={0}>
                  <CreateOrderPanel />
                </TabPanel>
              </>
            )}
          </TabPanels>
        </Tabs>
      </Box>
    </Box>
  );
};

// Dashboard Panel Component
const DashboardPanel = ({ isAdmin }) => {
  const [stats, setStats] = useState({
    totalOrders: 0,
    pendingOrders: 0,
    totalSpent: 0,
    recentOrders: [],
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        // Get all orders
        const ordersRes = await API.get('/orders');

        // Get pending orders
        const pendingRes = await API.get('/orders/unpaid');

        // Calculate total spent (for paid orders only)
        const totalSpent = ordersRes.data
          .filter(order => order.status === 'paid')
          .reduce((acc, order) => acc + order.totalAmount, 0);

        setStats({
          totalOrders: ordersRes.data.length,
          pendingOrders: pendingRes.data.length,
          totalSpent,
          recentOrders: ordersRes.data.slice(0, 5), // Get 5 most recent orders
        });

        setLoading(false);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const cardBg = useColorModeValue('white', 'gray.700');
  const textColor = useColorModeValue('gray.600', 'gray.200');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Heading size="md" mb={6}>
        {isAdmin ? 'Статистика системы' : 'Статистика клиники'}
      </Heading>

      <SimpleGrid columns={{ base: 1, md: 3 }} spacing={6} mb={6}>
        <StatCard
          title="Всего заказов"
          value={stats.totalOrders}
          icon={FiShoppingCart}
          accentColor="brand.500"
        />
        <StatCard
          title="Неоплаченные заказы"
          value={stats.pendingOrders}
          icon={FiAlertCircle}
          accentColor="orange.500"
        />
        <StatCard
          title="Сумма оплаченных заказов"
          value={`₽${stats.totalSpent.toLocaleString()}`}
          icon={FiDollarSign}
          accentColor="green.500"
        />
      </SimpleGrid>

      <Card bg={cardBg} boxShadow="sm" borderRadius="lg">
        <CardHeader>
          <Heading size="md">Последние заказы</Heading>
        </CardHeader>
        <CardBody>
          {stats.recentOrders.length > 0 ? (
            stats.recentOrders.map((order, index) => (
              <React.Fragment key={order._id}>
                <Flex justify="space-between" align="center" py={2}>
                  <Box>
                    <Text fontWeight="bold">Заказ #{order.orderNumber}</Text>
                    <Text fontSize="sm" color={textColor}>
                      {new Date(order.createdAt).toLocaleDateString()} • {order.items.length} {getPluralForm(order.items.length)}
                    </Text>
                  </Box>
                  <Box>
                    <Badge colorScheme={getStatusColorScheme(order.status)}>
                      {getStatusText(order.status)}
                    </Badge>
                    <Text fontWeight="medium" fontSize="md">
                      ₽{order.totalAmount.toLocaleString()}
                    </Text>
                  </Box>
                </Flex>
                {index < stats.recentOrders.length - 1 && <Divider />}
              </React.Fragment>
            ))
          ) : (
            <Text>Нет заказов</Text>
          )}
        </CardBody>
      </Card>
    </Box>
  );
};

// Clinics Panel Component (Admin only)
const ClinicsPanel = () => {
  const [clinics, setClinics] = useState([]);
  const [loading, setLoading] = useState(true);
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchClinics = async () => {
      try {
        const res = await API.get('/clinics');
        setClinics(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching clinics:', err);
        setLoading(false);
      }
    };

    fetchClinics();
  }, []);

  const filteredClinics = clinics.filter(
    clinic => clinic.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const cardBg = useColorModeValue('white', 'gray.700');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Flex justify="space-between" align="center" mb={4}>
        <Heading size="md">Управление клиниками</Heading>
        <Button leftIcon={<FiPlus />} colorScheme="brand" onClick={onOpen}>
          Добавить клинику
        </Button>
      </Flex>

      <InputGroup mb={4}>
        <InputLeftElement pointerEvents="none">
          <Icon as={FiSearch} color="gray.400" />
        </InputLeftElement>
        <Input
          placeholder="Поиск клиники..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </InputGroup>

      <Card bg={cardBg} boxShadow="sm" borderRadius="lg" overflow="hidden">
        <Table variant="simple">
          <Thead>
            <Tr>
              <Th>Название</Th>
              <Th>Контакты</Th>
              <Th width="120px">Действия</Th>
            </Tr>
          </Thead>
          <Tbody>
            {filteredClinics.map(clinic => (
              <Tr key={clinic._id}>
                <Td fontWeight="medium">{clinic.name}</Td>
                <Td>
                  <Text>{clinic.contact?.phone || '-'}</Text>
                  <Text fontSize="sm" color="gray.500">{clinic.contact?.email || '-'}</Text>
                </Td>
                <Td>
                  <HStack spacing={2}>
                    <IconButton
                      icon={<FiEdit />}
                      aria-label="Редактировать"
                      size="sm"
                      colorScheme="blue"
                      variant="ghost"
                    />
                    <IconButton
                      icon={<FiTrash2 />}
                      aria-label="Удалить"
                      size="sm"
                      colorScheme="red"
                      variant="ghost"
                    />
                  </HStack>
                </Td>
              </Tr>
            ))}
          </Tbody>
        </Table>
      </Card>

      {/* Add Clinic Modal */}
      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Добавить клинику</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <VStack spacing={4}>
              <FormControl isRequired>
                <FormLabel>Название клиники</FormLabel>
                <Input placeholder="Введите название" />
              </FormControl>
              <FormControl>
                <FormLabel>Телефон</FormLabel>
                <Input placeholder="Введите телефон" />
              </FormControl>
              <FormControl>
                <FormLabel>Email</FormLabel>
                <Input placeholder="Введите email" />
              </FormControl>
              <FormControl isRequired>
                <FormLabel>Имя пользователя</FormLabel>
                <Input placeholder="Введите имя пользователя" />
              </FormControl>
              <FormControl isRequired>
                <FormLabel>Пароль</FormLabel>
                <Input type="password" placeholder="Введите пароль" />
              </FormControl>
            </VStack>
          </ModalBody>
          <ModalFooter>
            <Button variant="outline" mr={3} onClick={onClose}>
              Отмена
            </Button>
            <Button colorScheme="brand">Сохранить</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </Box>
  );
};

// Products Panel Component (Admin only)
const ProductsPanel = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await API.get('/products');
        setProducts(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching products:', err);
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const filteredProducts = products.filter(
    product => product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const cardBg = useColorModeValue('white', 'gray.700');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Flex justify="space-between" align="center" mb={4}>
        <Heading size="md">Управление товарами</Heading>
        <Button leftIcon={<FiPlus />} colorScheme="brand" onClick={onOpen}>
          Добавить товар
        </Button>
      </Flex>

      <InputGroup mb={4}>
        <InputLeftElement pointerEvents="none">
          <Icon as={FiSearch} color="gray.400" />
        </InputLeftElement>
        <Input
          placeholder="Поиск товара..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </InputGroup>

      <Card bg={cardBg} boxShadow="sm" borderRadius="lg" overflow="hidden">
        <Table variant="simple">
          <Thead>
            <Tr>
              <Th>Название</Th>
              <Th>Статус</Th>
              <Th width="120px">Действия</Th>
            </Tr>
          </Thead>
          <Tbody>
            {filteredProducts.map(product => (
              <Tr key={product._id}>
                <Td fontWeight="medium">{product.name}</Td>
                <Td>
                  <Badge colorScheme={product.active ? 'green' : 'red'}>
                    {product.active ? 'Активен' : 'Неактивен'}
                  </Badge>
                </Td>
                <Td>
                  <HStack spacing={2}>
                    <IconButton
                      icon={<FiEdit />}
                      aria-label="Редактировать"
                      size="sm"
                      colorScheme="blue"
                      variant="ghost"
                    />
                    <IconButton
                      icon={<FiTrash2 />}
                      aria-label="Удалить"
                      size="sm"
                      colorScheme="red"
                      variant="ghost"
                    />
                  </HStack>
                </Td>
              </Tr>
            ))}
          </Tbody>
        </Table>
      </Card>

      {/* Add Product Modal */}
      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Добавить товар</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <VStack spacing={4}>
              <FormControl isRequired>
                <FormLabel>Название товара</FormLabel>
                <Input placeholder="Введите название" />
              </FormControl>
              <FormControl>
                <FormLabel>Статус</FormLabel>
                <Select defaultValue="true">
                  <option value="true">Активен</option>
                  <option value="false">Неактивен</option>
                </Select>
              </FormControl>
            </VStack>
          </ModalBody>
          <ModalFooter>
            <Button variant="outline" mr={3} onClick={onClose}>
              Отмена
            </Button>
            <Button colorScheme="brand">Сохранить</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </Box>
  );
};

// Orders Panel Component
const OrdersPanel = ({ isAdmin }) => {
  const [orders, setOrders] = useState([]);
  const [unpaidOrders, setUnpaidOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [searchType, setSearchType] = useState('orderNumber'); // 'orderNumber' or 'patientName'
  const [selectedClinic, setSelectedClinic] = useState('');
  const [clinics, setClinics] = useState([]);
  const [viewMode, setViewMode] = useState('all'); // 'all' or 'unpaid'

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);

        // Fetch all orders
        const ordersRes = await API.get('/orders');
        setOrders(ordersRes.data);

        // Fetch unpaid orders
        const unpaidRes = await API.get('/orders/unpaid');
        setUnpaidOrders(unpaidRes.data);

        // If admin, fetch clinics
        if (isAdmin) {
          const clinicsRes = await API.get('/clinics');
          setClinics(clinicsRes.data);
        }

        setLoading(false);
      } catch (err) {
        console.error('Error fetching orders:', err);
        setLoading(false);
      }
    };

    fetchData();
  }, [isAdmin]);

  const handleSearch = async () => {
    if (!searchTerm) return;

    try {
      setLoading(true);
      let res;

      if (searchType === 'patientName') {
        // Search by patient name
        const params = { name: searchTerm };
        if (isAdmin && selectedClinic) {
          params.clinicId = selectedClinic;
        }

        res = await API.get('/orders/search/patient', { params });
      } else {
        // Search by order number
        res = await API.get('/orders/search', { params: { term: searchTerm } });
      }

      setOrders(res.data);
      setViewMode('all');
      setLoading(false);
    } catch (err) {
      console.error('Error searching orders:', err);
      setLoading(false);
    }
  };

  const handleMarkAsPaid = async (orderId) => {
    try {
      await API.post(`/orders/${orderId}/pay`);

      // Update orders lists
      setOrders(orders.map(order => 
        order._id === orderId ? { ...order, status: 'paid', paymentDate: new Date() } : order
      ));

      setUnpaidOrders(unpaidOrders.filter(order => order._id !== orderId));
    } catch (err) {
      console.error('Error marking order as paid:', err);
    }
  };

  const resetSearch = async () => {
    try {
      setLoading(true);
      const res = await API.get('/orders');
      setOrders(res.data);
      setSearchTerm('');
      setViewMode('all');
      setLoading(false);
    } catch (err) {
      console.error('Error resetting search:', err);
      setLoading(false);
    }
  };

  // Get the current list based on view mode
  const currentList = viewMode === 'unpaid' ? unpaidOrders : orders;

  // Apply filters
  const filteredOrders = currentList
    .filter(order => statusFilter ? order.status === statusFilter : true)
    .filter(order => {
      if (selectedClinic && isAdmin) {
        return order.clinicId._id === selectedClinic;
      }
      return true;
    });

  const cardBg = useColorModeValue('white', 'gray.700');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Flex justify="space-between" align="center" mb={4}>
        <Heading size="md">Управление заказами</Heading>
        {!isAdmin && (
          <Button 
            leftIcon={<FiPlus />} 
            colorScheme="brand"
            onClick={() => setActiveTab(5)} // Navigate to CreateOrderPanel tab
          >
            Создать заказ
          </Button>
        )}
      </Flex>

      <Flex mb={4} gap={4} wrap="wrap">
        <HStack spacing={2} flex="1">
          <Select 
            value={searchType}
            onChange={e => setSearchType(e.target.value)}
            maxWidth={{ base: "100%", md: "200px" }}
          >
            <option value="orderNumber">№ заказа</option>
            <option value="patientName">ФИО пациента</option>
          </Select>

          <InputGroup flex="1">
            <InputLeftElement pointerEvents="none">
              <Icon as={FiSearch} color="gray.400" />
            </InputLeftElement>
            <Input
              placeholder={searchType === 'patientName' ? "Введите ФИО пациента..." : "Введите номер заказа..."}
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
              onKeyPress={e => e.key === 'Enter' && handleSearch()}
            />
          </InputGroup>

          <Button onClick={handleSearch} isDisabled={!searchTerm}>Найти</Button>
          {searchTerm && <Button variant="ghost" onClick={resetSearch}>Сбросить</Button>}
        </HStack>

        {isAdmin && (
          <Select 
            placeholder="Все клиники" 
            value={selectedClinic} 
            onChange={e => setSelectedClinic(e.target.value)}
            maxWidth={{ base: "100%", md: "200px" }}
          >
            {clinics.map(clinic => (
              <option key={clinic._id} value={clinic._id}>{clinic.name}</option>
            ))}
          </Select>
        )}

        <Select 
          placeholder="Все статусы" 
          value={statusFilter} 
          onChange={e => setStatusFilter(e.target.value)}
          maxWidth={{ base: "100%", md: "200px" }}
        >
          <option value="pending">Не оплачен</option>
          <option value="paid">Оплачен</option>
          <option value="cancelled">Отменен</option>
        </Select>

        <Button 
          variant={viewMode === 'all' ? 'solid' : 'outline'} 
          colorScheme="brand"
          onClick={() => setViewMode('all')}
        >
          Все заказы
        </Button>

        <Button 
          variant={viewMode === 'unpaid' ? 'solid' : 'outline'} 
          colorScheme="orange"
          onClick={() => setViewMode('unpaid')}
        >
          Неоплаченные
        </Button>
      </Flex>

      <Card bg={cardBg} boxShadow="sm" borderRadius="lg" overflow="hidden">
        <Table variant="simple">
          <Thead>
            <Tr>
              <Th>№ заказа</Th>
              {isAdmin && <Th>Клиника</Th>}
              <Th>Пациент</Th>
              <Th>Дата</Th>
              <Th>Срок сдачи</Th>
              <Th>Сумма</Th>
              <Th>Статус</Th>
              <Th width="120px">Действия</Th>
            </Tr>
          </Thead>
          <Tbody>
            {filteredOrders.length > 0 ? (
              filteredOrders.map(order => (
                <Tr key={order._id}>
                  <Td fontWeight="medium">#{order.orderNumber}</Td>
                  {isAdmin && <Td>{order.clinicId?.name || '-'}</Td>}
                  <Td>{order.patientName || '-'}</Td>
                  <Td>{new Date(order.createdAt).toLocaleDateString()}</Td>
                  <Td>{order.deadline ? new Date(order.deadline).toLocaleDateString() : '-'}</Td>
                  <Td>₽{order.totalAmount.toLocaleString()}</Td>
                  <Td>
                    <Badge colorScheme={getStatusColorScheme(order.status)}>
                      {getStatusText(order.status)}
                    </Badge>
                  </Td>
                  <Td>
                    <HStack spacing={2}>
                      {isAdmin && order.status === 'pending' && (
                        <IconButton
                          icon={<FiCheck />}
                          aria-label="Отметить как оплаченный"
                          size="sm"
                          colorScheme="green"
                          variant="ghost"
                          onClick={() => handleMarkAsPaid(order._id)}
                        />
                      )}
                      <IconButton
                        icon={<FiCornerDownRight />}
                        aria-label="Детали"
                        size="sm"
                        colorScheme="blue"
                        variant="ghost"
                      />
                    </HStack>
                  </Td>
                </Tr>
              ))
            ) : (
              <Tr>
                <Td colSpan={isAdmin ? 8 : 7} textAlign="center" py={4}>
                  Заказы не найдены
                </Td>
              </Tr>
            )}
          </Tbody>
        </Table>
      </Card>
    </Box>
  );
};

