import React, { useEffect, useState, useContext } from 'react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import {
  Box,
  Heading,
  Text,
  SimpleGrid,
  Stat,
  StatLabel,
  StatNumber,
  Card,
  CardHeader,
  CardBody,
  Button,
  Flex,
  Icon,
  Divider,
  useColorModeValue,
  Spinner,
  Center,
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
  HStack,
  VStack,
  Avatar,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  Input,
  Select,
  Badge,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  FormControl,
  FormLabel,
  IconButton,
  InputGroup,
  InputLeftElement,
  TableContainer,
} from '@chakra-ui/react';
import { FiShoppingCart, FiDollarSign, FiAlertCircle, FiUser, FiPackage, FiLogOut, FiSearch, FiPlus, FiEdit, FiTrash2, FiCornerDownRight, FiCheck } from 'react-icons/fi';
import API from '../utils/api';
import AuthContext from '../context/AuthContext';

const Dashboard = () => {
  const { user, isAdmin, logout } = useContext(AuthContext);
  const [activeTab, setActiveTab] = useState(0);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const cardBg = useColorModeValue('white', 'gray.700');
  const borderColor = useColorModeValue('gray.200', 'gray.700');

  // Load data based on active tab
  useEffect(() => {
    setLoading(false);
  }, [activeTab]);

  return (
    <Box>
      {/* Header */}
      <Flex 
        bg={cardBg} 
        p={4} 
        shadow="sm" 
        borderBottomWidth="1px" 
        borderColor={borderColor}
        justify="space-between"
        align="center"
        position="sticky"
        top={0}
        zIndex={10}
      >
        <Heading size="lg">Clinic Price Manager</Heading>

        <HStack spacing={4}>
          <Text>{user?.username}</Text>
          <Menu>
            <MenuButton 
              as={Avatar}
              size="sm"
              bg="brand.500"
              color="white"
              cursor="pointer"
            />
            <MenuList>
              <MenuItem 
                icon={<Icon as={FiLogOut} />} 
                onClick={handleLogout}
              >
                Выйти
              </MenuItem>
            </MenuList>
          </Menu>
        </HStack>
      </Flex>

      <Box p={4}>
        <Tabs 
          colorScheme="brand" 
          variant="enclosed" 
          onChange={(index) => setActiveTab(index)}
          isLazy
        >
          <TabList mb={4}>
            <Tab><Icon as={FiUser} mr={2} />Панель управления</Tab>

            {isAdmin ? (
              <>
                <Tab><Icon as={FiUser} mr={2} />Клиники</Tab>
                <Tab><Icon as={FiPackage} mr={2} />Товары</Tab>
                <Tab><Icon as={FiShoppingCart} mr={2} />Заказы</Tab>
              </>
            ) : (
              <>
                <Tab><Icon as={FiPackage} mr={2} />Прайс-лист</Tab>
                <Tab><Icon as={FiShoppingCart} mr={2} />Заказы</Tab>
                <Tab><Icon as={FiPlus} mr={2} />Создать заказ</Tab>
              </>
            )}
          </TabList>

          <TabPanels>
            {/* Dashboard Panel */}
            <TabPanel p={0}>
              <DashboardPanel isAdmin={isAdmin} />
            </TabPanel>

            {/* Admin Panels */}
            {isAdmin ? (
              <>
                <TabPanel p={0}>
                  <ClinicsPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <ProductsPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <OrdersPanel isAdmin={true} />
                </TabPanel>
              </>
            ) : (
              <>
                {/* Clinic Panels */}
                <TabPanel p={0}>
                  <PriceListPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <OrdersPanel isAdmin={false} />
                </TabPanel>
                <TabPanel p={0}>
                  <CreateOrderPanel />
                </TabPanel>
              </>
            )}
          </TabPanels>
        </Tabs>
      </Box>
    </Box>
  );
};

// Dashboard Panel Component
const DashboardPanel = ({ isAdmin }) => {
  const [stats, setStats] = useState({
    totalOrders: 0,
    pendingOrders: 0,
    totalSpent: 0,
    recentOrders: [],
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        // Get all orders
        const ordersRes = await API.get('/orders');

        // Get pending orders
        const pendingRes = await API.get('/orders/unpaid');

        // Calculate total spent (for paid orders only)
        const totalSpent = ordersRes.data
          .filter(order => order.status === 'paid')
          .reduce((acc, order) => acc + order.totalAmount, 0);

        setStats({
          totalOrders: ordersRes.data.length,
          pendingOrders: pendingRes.data.length,
          totalSpent,
          recentOrders: ordersRes.data.slice(0, 5), // Get 5 most recent orders
        });

        setLoading(false);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const cardBg = useColorModeValue('white', 'gray.700');
  const textColor = useColorModeValue('gray.600', 'gray.200');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Heading size="md" mb={6}>
        {isAdmin ? 'Статистика системы' : 'Статистика клиники'}
      </Heading>

      <SimpleGrid columns={{ base: 1, md: 3 }} spacing={6} mb={6}>
        <StatCard
          title="Всего заказов"
          value={stats.totalOrders}
          icon={FiShoppingCart}
          accentColor="brand.500"
        />
        <StatCard
          title="Неоплаченные заказы"
          value={stats.pendingOrders}
          icon={FiAlertCircle}
          accentColor="orange.500"
        />
        <StatCard
          title="Сумма оплаченных заказов"
          value={`₽${stats.totalSpent.toLocaleString()}`}
          icon={FiDollarSign}
          accentColor="green.500"
        />
      </SimpleGrid>

      <Card bg={cardBg} boxShadow="sm" borderRadius="lg">
        <CardHeader>
          <Heading size="md">Последние заказы</Heading>
        </CardHeader>
        <CardBody>
          {stats.recentOrders.length > 0 ? (
            stats.recentOrders.map((order, index) => (
              <React.Fragment key={order._id}>
                <Flex justify="space-between" align="center" py={2}>
                  <Box>
                    <Text fontWeight="bold">Заказ #{order.orderNumber}</Text>
                    <Text fontSize="sm" color={textColor}>
                      {new Date(order.createdAt).toLocaleDateString()} • {order.items.length} {getPluralForm(order.items.length)}
                    </Text>
                  </Box>
                  <Box>
                    <Badge colorScheme={getStatusColorScheme(order.status)}>
                      {getStatusText(order.status)}
                    </Badge>
                    <Text fontWeight="medium" fontSize="md">
                      ₽{order.totalAmount.toLocaleString()}
                    </Text>
                  </Box>
                </Flex>
                {index < stats.recentOrders.length - 1 && <Divider />}
              </React.Fragment>
            ))
          ) : (
            <Text>Нет заказов</Text>
          )}
        </CardBody>
      </Card>
    </Box>
  );
};

// Clinics Panel Component (Admin only)
const ClinicsPanel = () => {
  const [clinics, setClinics] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedClinic, setSelectedClinic] = useState(null);
  const [isPriceModalOpen, setIsPriceModalOpen] = useState(false);
  const [clinicPrices, setClinicPrices] = useState([]);
  const [loadingPrices, setLoadingPrices] = useState(false);

  useEffect(() => {
    fetchClinics();
  }, []);

  const fetchClinics = async () => {
    try {
      const res = await API.get('/clinics');
      setClinics(res.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching clinics:', err);
      setLoading(false);
    }
  };

  const fetchClinicPrices = async (clinicId) => {
    try {
      setLoadingPrices(true);
      // Запрос к API для получения цен данной клиники
      // Так как в текущем API нет прямого пути для получения цен клиники,
      // мы можем использовать запрос products с фильтрацией по клинике
      const res = await API.get(`/products?clinicId=${clinicId}`);
      setClinicPrices(res.data);
      setLoadingPrices(false);
    } catch (err) {
      console.error('Error fetching clinic prices:', err);
      setLoadingPrices(false);
    }
  };

  const handleOpenPrices = (clinic) => {
    setSelectedClinic(clinic);
    fetchClinicPrices(clinic._id);
    setIsPriceModalOpen(true);
  };

  const filteredClinics = searchTerm
    ? clinics.filter(clinic => 
        clinic.name.toLowerCase().includes(searchTerm.toLowerCase()))
    : clinics;

  const cardBg = useColorModeValue('white', 'gray.700');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Heading size="md" mb={6}>Управление клиниками</Heading>

      <Box mb={6}>
        <InputGroup>
          <InputLeftElement pointerEvents="none">
            <Icon as={FiSearch} color="gray.400" />
          </InputLeftElement>
          <Input
            placeholder="Поиск по названию клиники"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            bg="white"
            borderRadius="md"
          />
        </InputGroup>
      </Box>

      {filteredClinics.length > 0 ? (
        <TableContainer
          border="1px"
          borderColor={borderColor}
          borderRadius="lg"
          boxShadow="sm"
        >
          <Table variant="simple" bg={cardBg}>
            <Thead>
              <Tr>
                <Th>Название</Th>
                <Th>Контакты</Th>
                <Th>Статус</Th>
                <Th>Действия</Th>
              </Tr>
            </Thead>
            <Tbody>
              {filteredClinics.map(clinic => (
                <Tr key={clinic._id}>
                  <Td fontWeight="medium">{clinic.name}</Td>
                  <Td>
                    <VStack align="start" spacing={1}>
                      {clinic.contact?.phone && (
                        <Text fontSize="sm">{clinic.contact.phone}</Text>
                      )}
                      {clinic.contact?.email && (
                        <Text fontSize="sm">{clinic.contact.email}</Text>
                      )}
                      {!clinic.contact?.phone && !clinic.contact?.email && (
                        <Text fontSize="sm" color="gray.500">Не указаны</Text>
                      )}
                    </VStack>
                  </Td>
                  <Td>
                    <Badge colorScheme={clinic.active !== false ? 'green' : 'red'}>
                      {clinic.active !== false ? 'Активна' : 'Неактивна'}
                    </Badge>
                  </Td>
                  <Td>
                    <HStack spacing={2}>
                      <Button
                        size="sm"
                        colorScheme="brand"
                        variant="outline"
                        onClick={() => handleOpenPrices(clinic)}
                      >
                        Прайс-лист
                      </Button>
                    </HStack>
                  </Td>
                </Tr>
              ))}
            </Tbody>
          </Table>
        </TableContainer>
      ) : (
        <Box
          p={8}
          bg={cardBg}
          borderRadius="lg"
          textAlign="center"
          border="1px"
          borderColor={borderColor}
        >
          <Icon as={FiAlertCircle} boxSize={10} color="gray.400" mb={4} />
          <Heading size="md" mb={2}>
            Клиники не найдены
          </Heading>
          <Text color="gray.500">
            {searchTerm
              ? 'Не найдено клиник по вашему запросу'
              : 'В системе пока нет клиник'}
          </Text>
        </Box>
      )}

      {/* Модальное окно для отображения прайс-листа клиники */}
      {selectedClinic && (
        <Modal 
          isOpen={isPriceModalOpen} 
          onClose={() => setIsPriceModalOpen(false)}
          size="xl"
        >
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Прайс-лист клиники {selectedClinic.name}</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              {loadingPrices ? (
                <Center py={8}>
                  <Spinner size="lg" color="brand.500" thickness="4px" />
                </Center>
              ) : clinicPrices.length > 0 ? (
                <TableContainer>
                  <Table variant="simple" size="sm">
                    <Thead>
                      <Tr>
                        <Th>Название</Th>
                        <Th isNumeric>Цена (₽)</Th>
                      </Tr>
                    </Thead>
                    <Tbody>
                      {clinicPrices.map(item => (
                        <Tr key={item._id}>
                          <Td>{item.name}</Td>
                          <Td isNumeric fontWeight="bold">
                            {item.price !== null && item.price !== undefined
                              ? `₽${item.price.toLocaleString()}`
                              : <Badge colorScheme="red">Не установлена</Badge>
                            }
                          </Td>
                        </Tr>
                      ))}
                    </Tbody>
                  </Table>
                </TableContainer>
              ) : (
                <Center py={8}>
                  <VStack spacing={3}>
                    <Icon as={FiAlertCircle} boxSize={8} color="gray.400" />
                    <Text color="gray.500">Для этой клиники еще не настроены цены</Text>
                  </VStack>
                </Center>
              )}
            </ModalBody>
            <ModalFooter>
              <Button onClick={() => setIsPriceModalOpen(false)}>
                Закрыть
              </Button>
            </ModalFooter>
          </ModalContent>
        </Modal>
      )}
    </Box>
  );
};

// Products Panel Component (Admin only)
const ProductsPanel = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  useEffect(() => {
    fetchProductsData();
  }, []);

  const fetchProductsData = async () => {
    try {
      // Изменено с /products на /products/all для получения всех товаров (админская функция)
      const res = await API.get('/products/all');
      setProducts(res.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching products:', err);
      setLoading(false);
    }
  };

  const filteredProducts = products.filter(product => {
    const searchLower = searchTerm.toLowerCase();
    return (
      product.name.toLowerCase().includes(searchLower) ||
      (product.code && product.code.toLowerCase().includes(searchLower)) ||
      (product.description && product.description.toLowerCase().includes(searchLower))
    );
  });

  const handleProductUpdate = async (updatedProduct) => {
    try {
      await API.put(`/products/${updatedProduct._id}`, updatedProduct);
      setProducts(products.map(p => 
        p._id === updatedProduct._id ? updatedProduct : p
      ));
      setIsEditModalOpen(false);
      setSelectedProduct(null);
    } catch (err) {
      console.error('Error updating product:', err);
    }
  };

  const cardBg = useColorModeValue('white', 'gray.700');
  const borderColor = useColorModeValue('gray.200', 'gray.600');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Heading size="md" mb={6}>Управление товарами и услугами</Heading>

      <Box mb={6}>
        <InputGroup>
          <InputLeftElement pointerEvents="none">
            <Icon as={FiSearch} color="gray.400" />
          </InputLeftElement>
          <Input
            placeholder="Поиск по названию, коду или описанию"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            bg="white"
            borderRadius="md"
          />
        </InputGroup>
      </Box>

      <TableContainer
        border="1px"
        borderColor={borderColor}
        borderRadius="lg"
        boxShadow="sm"
      >
        <Table variant="simple" bg={cardBg}>
          <Thead>
            <Tr>
              <Th>Название</Th>
              <Th>Код</Th>
              <Th>Описание</Th>
              <Th>Статус</Th>
              <Th>Действия</Th>
            </Tr>
          </Thead>
          <Tbody>
            {filteredProducts.length > 0 ? (
              filteredProducts.map(product => (
                <Tr key={product._id}>
                  <Td fontWeight="medium">{product.name}</Td>
                  <Td fontFamily="mono">{product.code || '-'}</Td>
                  <Td>{product.description || '-'}</Td>
                  <Td>
                    <Badge colorScheme={product.active ? 'green' : 'red'}>
                      {product.active ? 'Активен' : 'Неактивен'}
                    </Badge>
                  </Td>
                  <Td>
                    <IconButton
                      icon={<FiEdit />}
                      size="sm"
                      colorScheme="brand"
                      variant="outline"
                      mr={2}
                      onClick={() => {
                        setSelectedProduct(product);
                        setIsEditModalOpen(true);
                      }}
                      aria-label="Edit product"
                    />
                  </Td>
                </Tr>
              ))
            ) : (
              <Tr>
                <Td colSpan={5} textAlign="center" py={4}>
                  {searchTerm ? 'Не найдено товаров по вашему запросу' : 'Нет доступных товаров'}
                </Td>
              </Tr>
            )}
          </Tbody>
        </Table>
      </TableContainer>

      {/* Edit Product Modal */}
      {selectedProduct && (
        <Modal isOpen={isEditModalOpen} onClose={() => setIsEditModalOpen(false)}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Редактировать товар</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              <VStack spacing={4}>
                <FormControl>
                  <FormLabel>Название</FormLabel>
                  <Input 
                    value={selectedProduct.name} 
                    onChange={(e) => setSelectedProduct({...selectedProduct, name: e.target.value})}
                  />
                </FormControl>
                <FormControl>
                  <FormLabel>Код</FormLabel>
                  <Input 
                    value={selectedProduct.code || ''} 
                    onChange={(e) => setSelectedProduct({...selectedProduct, code: e.target.value})}
                  />
                </FormControl>
                <FormControl>
                  <FormLabel>Описание</FormLabel>
                  <Input 
                    value={selectedProduct.description || ''} 
                    onChange={(e) => setSelectedProduct({...selectedProduct, description: e.target.value})}
                  />
                </FormControl>
                <FormControl>
                  <FormLabel>Статус</FormLabel>
                  <Select 
                    value={selectedProduct.active ? 'active' : 'inactive'}
                    onChange={(e) => setSelectedProduct({
                      ...selectedProduct, 
                      active: e.target.value === 'active'
                    })}
                  >
                    <option value="active">Активен</option>
                    <option value="inactive">Неактивен</option>
                  </Select>
                </FormControl>
              </VStack>
            </ModalBody>
            <ModalFooter>
              <Button variant="outline" mr={3} onClick={() => setIsEditModalOpen(false)}>
                Отмена
              </Button>
              <Button colorScheme="brand" onClick={() => handleProductUpdate(selectedProduct)}>
                Сохранить
              </Button>
            </ModalFooter>
          </ModalContent>
        </Modal>
      )}
    </Box>
  );
};

// Orders Panel Component
const OrdersPanel = ({ isAdmin }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [clinics, setClinics] = useState([]);
  const [selectedClinic, setSelectedClinic] = useState('');
  const [statusFilter, setStatusFilter] = useState('');

  useEffect(() => {
    fetchOrders();

    // Если это админ, загрузить список клиник
    if (isAdmin) {
      fetchClinics();
    }
  }, [isAdmin, selectedClinic, statusFilter]);

  const fetchOrders = async () => {
    try {
      let url = '/orders';
      const params = {};

      // Добавляем параметры фильтрации
      if (selectedClinic && isAdmin) {
        params.clinicId = selectedClinic;
      }

      if (statusFilter) {
        params.status = statusFilter;
      }

      // Если есть параметры, добавляем их к URL
      if (Object.keys(params).length > 0) {
        const queryString = new URLSearchParams(params).toString();
        url = `${url}?${queryString}`;
      }

      const res = await API.get(url);
      setOrders(res.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching orders:', err);
      setLoading(false);
    }
  };

  const fetchClinics = async () => {
    try {
      const res = await API.get('/clinics');
      setClinics(res.data);
    } catch (err) {
      console.error('Error fetching clinics:', err);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!searchTerm.trim()) {
      return fetchOrders();
    }

    try {
      setLoading(true);
      const res = await API.get(`/orders/search?term=${searchTerm}`);
      setOrders(res.data);
      setLoading(false);
    } catch (err) {
      console.error('Error searching orders:', err);
      setLoading(false);
    }
  };

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      if (newStatus === 'paid') {
        await API.post(`/orders/${orderId}/pay`);
      } else if (newStatus === 'cancelled') {
        await API.post(`/orders/${orderId}/cancel`);
      }

      // Обновляем список заказов
      fetchOrders();
    } catch (err) {
      console.error('Error updating order status:', err);
    }
  };

  const filteredOrders = searchTerm && !loading
    ? orders.filter(order => 
        order.orderNumber.toLowerCase().includes(searchTerm.toLowerCase()))
    : orders;

  const getStatusBadge = (status) => {
    switch (status) {
      case 'pending':
        return <Badge colorScheme="yellow">Ожидает оплаты</Badge>;
      case 'paid':
        return <Badge colorScheme="green">Оплачен</Badge>;
      case 'cancelled':
        return <Badge colorScheme="red">Отменен</Badge>;
      default:
        return <Badge>Неизвестно</Badge>;
    }
  };

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Heading size="md" mb={6}>
        {isAdmin ? 'Все заказы' : 'Ваши заказы'}
      </Heading>

      <Flex mb={6} direction={{ base: 'column', md: 'row' }} gap={4}>
        <HStack flex="1">
          <form onSubmit={handleSearch} style={{ width: '100%' }}>
            <InputGroup>
              <InputLeftElement pointerEvents="none">
                <Icon as={FiSearch} color="gray.400" />
              </InputLeftElement>
              <Input
                placeholder="Поиск по номеру заказа"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                bg="white"
                borderRadius="md"
              />
            </InputGroup>
          </form>
        </HStack>

        {isAdmin && (
          <Select
            placeholder="Выберите клинику"
            value={selectedClinic}
            onChange={(e) => setSelectedClinic(e.target.value)}
            bg="white"
            width={{ base: '100%', md: '250px' }}
          >
            <option value="">Все клиники</option>
            {clinics.map((clinic) => (
              <option key={clinic._id} value={clinic._id}>
                {clinic.name}
              </option>
            ))}
          </Select>
        )}

        <Select
          placeholder="Статус заказа"
          value={statusFilter}
          onChange={(e) => setStatusFilter(e.target.value)}
          bg="white"
          width={{ base: '100%', md: '200px' }}
        >
          <option value="">Все статусы</option>
          <option value="pending">Ожидает оплаты</option>
          <option value="paid">Оплачен</option>
          <option value="cancelled">Отменен</option>
        </Select>
      </Flex>

      {filteredOrders.length > 0 ? (
        <TableContainer
          border="1px"
          borderColor={borderColor}
          borderRadius="lg"
          boxShadow="sm"
        >
          <Table variant="simple" bg={cardBg}>
            <Thead>
              <Tr>
                <Th>№ заказа</Th>
                {isAdmin && <Th>Клиника</Th>}
                <Th>Дата</Th>
                <Th isNumeric>Сумма</Th>
                <Th>Статус</Th>
                {isAdmin && <Th>Действия</Th>}
              </Tr>
            </Thead>
            <Tbody>
              {filteredOrders.map((order) => (
                <Tr key={order._id}>
                  <Td fontWeight="bold">{order.orderNumber}</Td>
                  {isAdmin && (
                    <Td>{order.clinicId?.name || 'Неизвестно'}</Td>
                  )}
                  <Td>{new Date(order.createdAt).toLocaleDateString()}</Td>
                  <Td isNumeric fontWeight="medium">
                    ₽{order.totalAmount?.toLocaleString() || 0}
                  </Td>
                  <Td>{getStatusBadge(order.status)}</Td>
                  {isAdmin && (
                    <Td>
                      <Menu>
                        <MenuButton
                          as={Button}
                          size="sm"
                          rightIcon={<FiCornerDownRight />}
                          variant="outline"
                        >
                          Действия
                        </MenuButton>
                        <MenuList>
                          {order.status === 'pending' && (
                            <>
                              <MenuItem
                                icon={<FiCheck />}
                                onClick={() => handleStatusChange(order._id, 'paid')}
                              >
                                Отметить как оплаченный
                              </MenuItem>
                              <MenuItem
                                icon={<FiTrash2 />}
                                onClick={() => handleStatusChange(order._id, 'cancelled')}
                              >
                                Отменить заказ
                              </MenuItem>
                            </>
                          )}
                        </MenuList>
                      </Menu>
                    </Td>
                  )}
                </Tr>
              ))}
            </Tbody>
          </Table>
        </TableContainer>
      ) : (
        <Box
          p={8}
          bg={cardBg}
          borderRadius="lg"
          textAlign="center"
          border="1px"
          borderColor={borderColor}
        >
          <Icon as={FiAlertCircle} boxSize={10} color="gray.400" mb={4} />
          <Heading size="md" mb={2}>
            Заказы не найдены
          </Heading>
          <Text color="gray.500">
            {searchTerm
              ? 'Не найдено заказов по вашему запросу'
              : isAdmin
              ? 'В системе пока нет заказов'
              : 'У вас пока нет заказов'}
          </Text>
        </Box>
      )}
    </Box>
  );
};

