import React, { useEffect, useState, useContext } from 'react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import {
  Box,
  Heading,
  Text,
  SimpleGrid,
  Stat,
  StatLabel,
  StatNumber,
  Card,
  CardHeader,
  CardBody,
  Button,
  Flex,
  Icon,
  Divider,
  useColorModeValue,
  Spinner,
  Center,
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
  HStack,
  VStack,
  Avatar,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  Input,
  Select,
  Badge,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  FormControl,
  FormLabel,
  IconButton,
  InputGroup,
  InputLeftElement,
} from '@chakra-ui/react';
import { FiShoppingCart, FiDollarSign, FiAlertCircle, FiUser, FiPackage, FiLogOut, FiSearch, FiPlus, FiEdit, FiTrash2, FiCornerDownRight, FiCheck, FiX } from 'react-icons/fi';
import API from '../utils/api';
import AuthContext from '../context/AuthContext';

const Dashboard = () => {
  const { user, isAdmin, logout } = useContext(AuthContext);
  const [activeTab, setActiveTab] = useState(0);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const cardBg = useColorModeValue('white', 'gray.700');
  const borderColor = useColorModeValue('gray.200', 'gray.700');

  // Load data based on active tab
  useEffect(() => {
    setLoading(false);
  }, [activeTab]);

  return (
    <Box>
      {/* Header */}
      <Flex 
        bg={cardBg} 
        p={4} 
        shadow="sm" 
        borderBottomWidth="1px" 
        borderColor={borderColor}
        justify="space-between"
        align="center"
        position="sticky"
        top={0}
        zIndex={10}
      >
        <Heading size="lg">Dental Lab Manager</Heading>

        <HStack spacing={4}>
          <Text>{user?.username}</Text>
          <Menu>
            <MenuButton 
              as={Avatar}
              size="sm"
              bg="brand.500"
              color="white"
              cursor="pointer"
            />
            <MenuList>
              <MenuItem 
                icon={<Icon as={FiLogOut} />} 
                onClick={handleLogout}
              >
                Выйти
              </MenuItem>
            </MenuList>
          </Menu>
        </HStack>
      </Flex>

      <Box p={4}>
        <Tabs 
          colorScheme="brand" 
          variant="enclosed" 
          onChange={(index) => setActiveTab(index)}
          isLazy
          index={activeTab}
        >
          <TabList mb={4}>
            <Tab><Icon as={FiUser} mr={2} />Панель управления</Tab>

            {isAdmin ? (
              <>
                <Tab><Icon as={FiUser} mr={2} />Клиники</Tab>
                <Tab><Icon as={FiPackage} mr={2} />Товары</Tab>
                <Tab><Icon as={FiShoppingCart} mr={2} />Заказы</Tab>
              </>
            ) : (
              <>
                <Tab><Icon as={FiPackage} mr={2} />Прайс-лист</Tab>
                <Tab><Icon as={FiShoppingCart} mr={2} />Заказы</Tab>
                <Tab><Icon as={FiPlus} mr={2} />Создать заказ</Tab>
              </>
            )}
          </TabList>

          <TabPanels>
            {/* Dashboard Panel */}
            <TabPanel p={0}>
              <DashboardPanel isAdmin={isAdmin} setActiveTab={setActiveTab} />
            </TabPanel>

            {/* Admin Panels */}
            {isAdmin ? (
              <>
                <TabPanel p={0}>
                  <ClinicsPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <ProductsPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <OrdersPanel isAdmin={true} />
                </TabPanel>
              </>
            ) : (
              <>
                {/* Clinic Panels */}
                <TabPanel p={0}>
                  <PriceListPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <OrdersPanel isAdmin={false} />
                </TabPanel>
                <TabPanel p={0}>
                  <CreateOrderPanel setActiveTab={setActiveTab} />
                </TabPanel>
              </>
            )}
          </TabPanels>
        </Tabs>
      </Box>
    </Box>
  );
};

// Dashboard Panel Component
const DashboardPanel = ({ isAdmin, setActiveTab }) => {
  const [stats, setStats] = useState({
    totalOrders: 0,
    pendingOrders: 0,
    totalSpent: 0,
    recentOrders: [],
  });
  const [unpaidOrders, setUnpaidOrders] = useState([]);
  const [selectedClinic, setSelectedClinic] = useState('');
  const [clinics, setClinics] = useState([]);
  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const fetchStats = async () => {
      try {
        setLoading(true);
        setErrorMessage('');

        // Get all orders
        const ordersRes = await API.get('/orders');

        // Get pending orders
        const pendingRes = await API.get('/orders/unpaid');

        // Calculate summary statistics
        const totalOrders = ordersRes.data.length;
        const pendingOrders = pendingRes.data.length;

        // Calculate total amount of paid orders
        const paidOrders = ordersRes.data.filter(order => order.status === 'paid');
        const totalSpent = paidOrders.reduce((sum, order) => sum + order.totalAmount, 0);

        // Recent orders (last 5)
        const recentOrders = ordersRes.data.slice(0, 5);

        setStats({
          totalOrders,
          pendingOrders,
          totalSpent,
          recentOrders
        });

        setUnpaidOrders(pendingRes.data);

        // If admin, fetch clinics
        if (isAdmin) {
          const clinicsRes = await API.get('/clinics');
          setClinics(clinicsRes.data);
        }

        setLoading(false);
      } catch (err) {
        console.error('Error fetching stats:', err);
        setErrorMessage('Ошибка загрузки данных: ' + (err.response?.data?.message || err.message));
        setLoading(false);
      }
    };

    fetchStats();
  }, [isAdmin]);

  const handleMarkAsPaid = async (orderId) => {
    try {
      setErrorMessage('');
      await API.post(`/orders/${orderId}/pay`);

      // Update orders lists
      setUnpaidOrders(unpaidOrders.filter(order => order._id !== orderId));

      // Update stats
      setStats(prev => ({
        ...prev,
        pendingOrders: prev.pendingOrders - 1
      }));
    } catch (err) {
      console.error('Error marking order as paid:', err);
      setErrorMessage('Ошибка при оплате заказа: ' + (err.response?.data?.message || err.message));
    }
  };

  const goToAllOrders = () => {
    // Navigate to Orders tab (index 3 for admin, 1 for clinic)
    const tabIndex = isAdmin ? 3 : 1;
    setActiveTab(tabIndex);
  };

  const filteredOrders = selectedClinic 
    ? unpaidOrders.filter(order => order.clinicId._id === selectedClinic)
    : unpaidOrders;

  const cardBg = useColorModeValue('white', 'gray.700');

  if (loading && stats.recentOrders.length === 0) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      {errorMessage && (
        <Box mb={6} p={4} borderRadius="md" bg="red.100" color="red.800">
          {errorMessage}
        </Box>
      )}

      <SimpleGrid columns={{ base: 1, md: 3 }} spacing={6} mb={6}>
        <StatCard 
          title="Всего заказов" 
          value={stats.totalOrders}
          icon={FiShoppingCart}
          accentColor="brand.500"
          onClick={goToAllOrders}
        />
        <StatCard 
          title="Неоплаченных заказов" 
          value={stats.pendingOrders}
          icon={FiAlertCircle}
          accentColor="orange.500"
          onClick={goToAllOrders}
        />
        <StatCard 
          title="Общая сумма оплаченных" 
          value={`₽${stats.totalSpent.toLocaleString()}`}
          icon={FiDollarSign}
          accentColor="green.500"
        />
      </SimpleGrid>

      {isAdmin ? (
        // Admin dashboard
        <Card bg={cardBg} boxShadow="sm" borderRadius="lg">
          <CardHeader pb={2}>
            <Flex justify="space-between" align="center">
              <Heading size="md">Неоплаченные заказы</Heading>

              <Select 
                placeholder="Все клиники" 
                value={selectedClinic} 
                onChange={e => setSelectedClinic(e.target.value)}
                width="250px"
              >
                {clinics.map(clinic => (
                  <option key={clinic._id} value={clinic._id}>{clinic.name}</option>
                ))}
              </Select>
            </Flex>
          </CardHeader>
          <CardBody>
            {loading ? (
              <Center py={8}>
                <Spinner size="lg" color="brand.500" thickness="4px" />
              </Center>
            ) : filteredOrders.length > 0 ? (
              <Table variant="simple">
                <Thead>
                  <Tr>
                    <Th>№ заказа</Th>
                    <Th>Клиника</Th>
                    <Th>Пациент</Th>
                    <Th>Дата</Th>
                    <Th>Срок сдачи</Th>
                    <Th isNumeric>Сумма</Th>
                    <Th width="100px">Действия</Th>
                  </Tr>
                </Thead>
                <Tbody>
                  {filteredOrders.map(order => (
                    <Tr key={order._id}>
                      <Td fontWeight="medium">#{order.orderNumber}</Td>
                      <Td>{order.clinicId?.name || '-'}</Td>
                      <Td>{order.patientName || '-'}</Td>
                      <Td>{new Date(order.createdAt).toLocaleDateString()}</Td>
                      <Td>{order.deadline ? new Date(order.deadline).toLocaleDateString() : '-'}</Td>
                      <Td isNumeric>₽{order.totalAmount.toLocaleString()}</Td>
                      <Td>
                        <Button 
                          size="sm" 
                          colorScheme="green"
                          leftIcon={<FiCheck />}
                          onClick={() => handleMarkAsPaid(order._id)}
                        >
                          Оплачен
                        </Button>
                      </Td>
                    </Tr>
                  ))}
                </Tbody>
              </Table>
            ) : (
              <Center py={8}>
                <Text color="gray.500">Нет неоплаченных заказов</Text>
              </Center>
            )}

            {filteredOrders.length > 0 && (
              <Flex justifyContent="center" mt={4}>
                <Button 
                  colorScheme="brand" 
                  variant="outline"
                  onClick={goToAllOrders}
                  leftIcon={<FiShoppingCart />}
                >
                  Все заказы
                </Button>
              </Flex>
            )}
          </CardBody>
        </Card>
      ) : (
        // Clinic dashboard
        <Card bg={cardBg} boxShadow="sm" borderRadius="lg">
          <CardHeader pb={2}>
            <Heading size="md">Последние заказы</Heading>
          </CardHeader>
          <CardBody>
            {loading ? (
              <Center py={8}>
                <Spinner size="lg" color="brand.500" thickness="4px" />
              </Center>
            ) : stats.recentOrders.length > 0 ? (
              <Table variant="simple">
                <Thead>
                  <Tr>
                    <Th>№ заказа</Th>
                    <Th>Пациент</Th>
                    <Th>Дата</Th>
                    <Th isNumeric>Сумма</Th>
                    <Th>Статус</Th>
                  </Tr>
                </Thead>
                <Tbody>
                  {stats.recentOrders.map(order => (
                    <Tr key={order._id}>
                      <Td fontWeight="medium">#{order.orderNumber}</Td>
                      <Td>{order.patientName || '-'}</Td>
                      <Td>{new Date(order.createdAt).toLocaleDateString()}</Td>
                      <Td isNumeric>₽{order.totalAmount.toLocaleString()}</Td>
                      <Td>
                        <Badge colorScheme={getStatusColorScheme(order.status)}>
                          {getStatusText(order.status)}
                        </Badge>
                      </Td>
                    </Tr>
                  ))}
                </Tbody>
              </Table>
            ) : (
              <Center py={8}>
                <Text color="gray.500">Заказы не найдены</Text>
              </Center>
            )}

            <HStack spacing={4} justifyContent="center" mt={4}>
              <Button 
                colorScheme="brand" 
                variant="outline"
                onClick={goToAllOrders}
                leftIcon={<FiShoppingCart />}
              >
                Все заказы
              </Button>

              <Button 
                colorScheme="brand"
                onClick={() => setActiveTab(2)} // Navigate to Create Order tab
                leftIcon={<FiPlus />}
              >
                Создать заказ
              </Button>
            </HStack>
          </CardBody>
        </Card>
      )}
    </Box>
  );
};

// Clinics Panel Component (Admin only)
const ClinicsPanel = () => {
  const [clinics, setClinics] = useState([]);
  const [loading, setLoading] = useState(true);
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchClinics = async () => {
      try {
        const res = await API.get('/clinics');
        setClinics(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching clinics:', err);
        setLoading(false);
      }
    };

    fetchClinics();
  }, []);

  const filteredClinics = clinics.filter(
    clinic => clinic.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const cardBg = useColorModeValue('white', 'gray.700');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Flex justify="space-between" align="center" mb={4}>
        <Heading size="md">Управление клиниками</Heading>
        <Button leftIcon={<FiPlus />} colorScheme="brand" onClick={onOpen}>
          Добавить клинику
        </Button>
      </Flex>

      <InputGroup mb={4}>
        <InputLeftElement pointerEvents="none">
          <Icon as={FiSearch} color="gray.400" />
        </InputLeftElement>
        <Input
          placeholder="Поиск клиники..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </InputGroup>

      <Card bg={cardBg} boxShadow="sm" borderRadius="lg" overflow="hidden">
        <Table variant="simple">
          <Thead>
            <Tr>
              <Th>Название</Th>
              <Th>Контакты</Th>
              <Th width="120px">Действия</Th>
            </Tr>
          </Thead>
          <Tbody>
            {filteredClinics.map(clinic => (
              <Tr key={clinic._id}>
                <Td fontWeight="medium">{clinic.name}</Td>
                <Td>
                  <Text>{clinic.contact?.phone || '-'}</Text>
                  <Text fontSize="sm" color="gray.500">{clinic.contact?.email || '-'}</Text>
                </Td>
                <Td>
                  <HStack spacing={2}>
                    <IconButton
                      icon={<FiEdit />}
                      aria-label="Редактировать"
                      size="sm"
                      colorScheme="blue"
                      variant="ghost"
                    />
                    <IconButton
                      icon={<FiTrash2 />}
                      aria-label="Удалить"
                      size="sm"
                      colorScheme="red"
                      variant="ghost"
                    />
                  </HStack>
                </Td>
              </Tr>
            ))}
          </Tbody>
        </Table>
      </Card>

      {/* Add Clinic Modal */}
      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Добавить клинику</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <VStack spacing={4}>
              <FormControl isRequired>
                <FormLabel>Название клиники</FormLabel>
                <Input placeholder="Введите название" />
              </FormControl>
              <FormControl>
                <FormLabel>Телефон</FormLabel>
                <Input placeholder="Введите телефон" />
              </FormControl>
              <FormControl>
                <FormLabel>Email</FormLabel>
                <Input placeholder="Введите email" />
              </FormControl>
              <FormControl isRequired>
                <FormLabel>Имя пользователя</FormLabel>
                <Input placeholder="Введите имя пользователя" />
              </FormControl>
              <FormControl isRequired>
                <FormLabel>Пароль</FormLabel>
                <Input type="password" placeholder="Введите пароль" />
              </FormControl>
            </VStack>
          </ModalBody>
          <ModalFooter>
            <Button variant="outline" mr={3} onClick={onClose}>
              Отмена
            </Button>
            <Button colorScheme="brand">Сохранить</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </Box>
  );
};

// Products Panel Component (Admin only)
const ProductsPanel = () => {
  const [products, setProducts] = useState([]);
  const [prices, setPrices] = useState({});
  const [clinics, setClinics] = useState([]);
  const [selectedClinic, setSelectedClinic] = useState('');
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [editingPrice, setEditingPrice] = useState(null);
  const [newPrice, setNewPrice] = useState('');
  const [saveLoading, setSaveLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [newProduct, setNewProduct] = useState({ name: '', code: '', description: '' });

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // Fetch products
        const productsRes = await API.get('/products/all');
        setProducts(productsRes.data);

        // Fetch clinics
        const clinicsRes = await API.get('/clinics');
        setClinics(clinicsRes.data);

        if (clinicsRes.data.length > 0) {
          setSelectedClinic(clinicsRes.data[0]._id);
        }

        setLoading(false);
      } catch (err) {
        console.error('Error fetching products:', err);
        setErrorMessage('Ошибка загрузки товаров: ' + (err.response?.data?.message || err.message));
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    const fetchPrices = async () => {
      if (!selectedClinic) return;

      try {
        setLoading(true);
        const res = await API.get('/prices', { params: { clinicId: selectedClinic } });

        // Convert to map for easier access
        const priceMap = {};
        res.data.forEach(price => {
          priceMap[price.product._id] = { 
            _id: price._id, 
            price: price.price 
          };
        });

        setPrices(priceMap);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching prices:', err);
        setErrorMessage('Ошибка загрузки цен: ' + (err.response?.data?.message || err.message));
        setLoading(false);
      }
    };

    if (selectedClinic) {
      fetchPrices();
    }
  }, [selectedClinic]);

  const handleEditPrice = (productId, currentPrice) => {
    setEditingPrice(productId);
    setNewPrice(currentPrice || '');
  };

  const handleSavePrice = async (productId) => {
    if (!newPrice || isNaN(parseFloat(newPrice)) || parseFloat(newPrice) < 0) {
      setErrorMessage('Введите корректную цену');
      return;
    }

    setSaveLoading(true);
    setErrorMessage('');

    try {
      const currentPrice = prices[productId];

      if (currentPrice && currentPrice._id) {
        // Update existing price
        await API.put(`/prices/${currentPrice._id}`, {
          price: parseFloat(newPrice)
        });
      } else {
        // Create new price
        const res = await API.post('/prices', {
          clinicId: selectedClinic,
          productId,
          price: parseFloat(newPrice)
        });

        // Update prices map with the new price entry
        setPrices(prev => ({
          ...prev,
          [productId]: { _id: res.data._id, price: parseFloat(newPrice) }
        }));
      }

      // Update local state
      setPrices(prev => ({
        ...prev,
        [productId]: { ...prev[productId], price: parseFloat(newPrice) }
      }));

      setEditingPrice(null);
      setNewPrice('');

      // Show success message
      setSuccessMessage('Цена успешно обновлена');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      console.error('Error saving price:', err);
      setErrorMessage('Ошибка сохранения цены: ' + (err.response?.data?.message || err.message));
    } finally {
      setSaveLoading(false);
    }
  };

  const cancelEditing = () => {
    setEditingPrice(null);
    setNewPrice('');
    setErrorMessage('');
  };

  const handleAddProduct = async () => {
    try {
      if (!newProduct.name || !newProduct.code) {
        setErrorMessage('Введите название и код товара');
        return;
      }

      const res = await API.post('/products', newProduct);
      setProducts([...products, res.data]);
      setNewProduct({ name: '', code: '', description: '' });
      onClose();
      setSuccessMessage('Товар успешно добавлен');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      console.error('Error adding product:', err);
      setErrorMessage('Ошибка добавления товара: ' + (err.response?.data?.message || err.message));
    }
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    (product.code && product.code.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const cardBg = useColorModeValue('white', 'gray.700');

  if (loading && products.length === 0) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Heading size="md" mb={6}>Управление товарами и ценами</Heading>

      {errorMessage && (
        <Box mb={6} p={4} borderRadius="md" bg="red.100" color="red.800">
          {errorMessage}
        </Box>
      )}

      {successMessage && (
        <Box mb={6} p={4} borderRadius="md" bg="green.100" color="green.800">
          {successMessage}
        </Box>
      )}

      <Flex justify="space-between" align="center" mb={4}>
        <Flex wrap="wrap" gap={4} flex="1">
          <Select 
            placeholder="Выберите клинику" 
            value={selectedClinic} 
            onChange={e => setSelectedClinic(e.target.value)}
            maxWidth={{ base: "100%", md: "300px" }}
          >
            {clinics.map(clinic => (
              <option key={clinic._id} value={clinic._id}>{clinic.name}</option>
            ))}
          </Select>

          <InputGroup flex="1">
            <InputLeftElement pointerEvents="none">
              <Icon as={FiSearch} color="gray.400" />
            </InputLeftElement>
            <Input
              placeholder="Поиск товара..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
          </InputGroup>
        </Flex>

        <Button 
          leftIcon={<FiPlus />} 
          colorScheme="brand" 
          onClick={onOpen}
          ml={4}
        >
          Добавить товар
        </Button>
      </Flex>

      {loading ? (
        <Center py={8}>
          <Spinner size="lg" color="brand.500" thickness="4px" />
        </Center>
      ) : (
        <Card bg={cardBg} boxShadow="sm" borderRadius="lg" overflow="hidden">
          {filteredProducts.length > 0 ? (
            <Table variant="simple">
              <Thead>
                <Tr>
                  <Th>Код</Th>
                  <Th>Название</Th>
                  <Th isNumeric>Цена</Th>
                  <Th width="120px">Действия</Th>
                </Tr>
              </Thead>
              <Tbody>
                {filteredProducts.map(product => (
                  <Tr key={product._id}>
                    <Td fontWeight="medium">{product.code}</Td>
                    <Td>{product.name}</Td>
                    <Td isNumeric>
                      {editingPrice === product._id ? (
                        <Input 
                          type="number" 
                          value={newPrice} 
                          onChange={e => setNewPrice(e.target.value)}
                          size="sm"
                          width="120px"
                          ml="auto"
                          autoFocus
                          onKeyPress={e => e.key === 'Enter' && handleSavePrice(product._id)}
                        />
                      ) : (
                        prices[product._id] ? `₽${prices[product._id].price.toLocaleString()}` : '-'
                      )}
                    </Td>
                    <Td>
                      {editingPrice === product._id ? (
                        <HStack spacing={1}>
                          <IconButton
                            icon={<FiCheck />}
                            aria-label="Сохранить"
                            size="sm"
                            colorScheme="green"
                            isLoading={saveLoading}
                            onClick={() => handleSavePrice(product._id)}
                          />
                          <IconButton
                            icon={<FiX />}
                            aria-label="Отменить"
                            size="sm"
                            colorScheme="red"
                            onClick={cancelEditing}
                          />
                        </HStack>
                      ) : (
                        <IconButton
                          icon={<FiEdit />}
                          aria-label="Редактировать цену"
                          size="sm"
                          colorScheme="blue"
                          variant="ghost"
                          onClick={() => handleEditPrice(product._id, prices[product._id]?.price || '')}
                        />
                      )}
                    </Td>
                  </Tr>
                ))}
              </Tbody>
            </Table>
          ) : (
            <Center py={8}>
              <VStack>
                <Icon as={FiPackage} fontSize="4xl" color="gray.400" />
                <Text color="gray.500">Товары не найдены</Text>
                <Text fontSize="sm" color="gray.400">
                  Добавьте товары или измените параметры поиска
                </Text>
              </VStack>
            </Center>
          )}
        </Card>
      )}

      {/* Add Product Modal */}
      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Добавить товар</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <VStack spacing={4}>
              <FormControl isRequired>
                <FormLabel>Название товара</FormLabel>
                <Input 
                  placeholder="Введите название" 
                  value={newProduct.name}
                  onChange={(e) => setNewProduct({...newProduct, name: e.target.value})}
                />
              </FormControl>
              <FormControl isRequired>
                <FormLabel>Код товара</FormLabel>
                <Input 
                  placeholder="Введите код" 
                  value={newProduct.code}
                  onChange={(e) => setNewProduct({...newProduct, code: e.target.value})}
                />
              </FormControl>
              <FormControl>
                <FormLabel>Описание</FormLabel>
                <Input 
                  placeholder="Введите описание" 
                  value={newProduct.description}
                  onChange={(e) => setNewProduct({...newProduct, description: e.target.value})}
                />
              </FormControl>
            </VStack>
          </ModalBody>
          <ModalFooter>
            <Button variant="outline" mr={3} onClick={onClose}>
              Отмена
            </Button>
            <Button colorScheme="brand" onClick={handleAddProduct}>
              Сохранить
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </Box>
  );
};

// Orders Panel Component
const OrdersPanel = ({ isAdmin }) => {
  const [orders, setOrders] = useState([]);
  const [unpaidOrders, setUnpaidOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [searchType, setSearchType] = useState('orderNumber'); // 'orderNumber' or 'patientName'
  const [selectedClinic, setSelectedClinic] = useState('');
  const [clinics, setClinics] = useState([]);
  const [viewMode, setViewMode] = useState('all'); // 'all' or 'unpaid'
  const [orderDetails, setOrderDetails] = useState(null);
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setErrorMessage('');

        // Fetch all orders
        const ordersRes = await API.get('/orders');
        setOrders(ordersRes.data);

        // Fetch unpaid orders
        const unpaidRes = await API.get('/orders/unpaid');
        setUnpaidOrders(unpaidRes.data);

        // If admin, fetch clinics
        if (isAdmin) {
          const clinicsRes = await API.get('/clinics');
          setClinics(clinicsRes.data);
        }

        setLoading(false);
      } catch (err) {
        console.error('Error fetching orders:', err);
        setErrorMessage('Ошибка загрузки заказов: ' + (err.response?.data?.message || err.message));
        setLoading(false);
      }
    };

    fetchData();
  }, [isAdmin]);

  const handleSearch = async () => {
    if (!searchTerm) return;

    try {
      setLoading(true);
      setErrorMessage('');
      let res;

      if (searchType === 'patientName') {
        // Search by patient name
        const params = { name: searchTerm };
        if (isAdmin && selectedClinic) {
          params.clinicId = selectedClinic;
        }

        res = await API.get('/orders/search/patient', { params });
      } else {
        // Search by order number
        res = await API.get('/orders/search', { params: { term: searchTerm } });
      }

      setOrders(res.data);
      setViewMode('all');
      setLoading(false);
    } catch (err) {
      console.error('Error searching orders:', err);
      setErrorMessage('Ошибка поиска: ' + (err.response?.data?.message || err.message));
      setLoading(false);
    }
  };

  const handleMarkAsPaid = async (orderId) => {
    try {
      setErrorMessage('');
      await API.post(`/orders/${orderId}/pay`);

      // Success message
      setSuccessMessage('Заказ отмечен как оплаченный');
      setTimeout(() => setSuccessMessage(''), 3000);

      // Update orders lists
      setOrders(orders.map(order => 
        order._id === orderId ? { ...order, status: 'paid', paymentDate: new Date() } : order
      ));

      setUnpaidOrders(unpaidOrders.filter(order => order._id !== orderId));
    } catch (err) {
      console.error('Error marking order as paid:', err);
      setErrorMessage('Ошибка при оплате заказа: ' + (err.response?.data?.message || err.message));
    }
  };

  const resetSearch = async () => {
    try {
      setLoading(true);
      setErrorMessage('');
      const res = await API.get('/orders');
      setOrders(res.data);
      setSearchTerm('');
      setViewMode('all');
      setLoading(false);
    } catch (err) {
      console.error('Error resetting search:', err);
      setErrorMessage('Ошибка: ' + (err.response?.data?.message || err.message));
      setLoading(false);
    }
  };

  const viewOrderDetails = (order) => {
    setOrderDetails(order);
    onOpen();
  };

  // Get the current list based on view mode
  const currentList = viewMode === 'unpaid' ? unpaidOrders : orders;

  // Apply filters
  const filteredOrders = currentList
    .filter(order => statusFilter ? order.status === statusFilter : true)
    .filter(order => {
      if (selectedClinic && isAdmin) {
        return order.clinicId._id === selectedClinic;
      }
      return true;
    });

  const cardBg = useColorModeValue('white', 'gray.700');

  if (loading && orders.length === 0 && unpaidOrders.length === 0) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Flex justify="space-between" align="center" mb={4}>
        <Heading size="md">Управление заказами</Heading>
        {!isAdmin && (
          <Button 
            leftIcon={<FiPlus />} 
            colorScheme="brand"
            as={RouterLink}
            to="/create-order"
          >
            Создать заказ
          </Button>
        )}
      </Flex>

      {errorMessage && (
        <Box mb={6} p={4} borderRadius="md" bg="red.100" color="red.800">
          {errorMessage}
        </Box>
      )}

      {successMessage && (
        <Box mb={6} p={4} borderRadius="md" bg="green.100" color="green.800">
          {successMessage}
        </Box>
      )}

      <Flex mb={4} gap={4} wrap="wrap">
        <HStack spacing={2} flex="1">
          <Select 
            value={searchType}
            onChange={e => setSearchType(e.target.value)}
            maxWidth={{ base: "100%", md: "200px" }}
          >
            <option value="orderNumber">№ заказа</option>
            <option value="patientName">ФИО пациента</option>
          </Select>

          <InputGroup flex="1">
            <InputLeftElement pointerEvents="none">
              <Icon as={FiSearch} color="gray.400" />
            </InputLeftElement>
            <Input
              placeholder={searchType === 'patientName' ? "Введите ФИО пациента..." : "Введите номер заказа..."}
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
              onKeyPress={e => e.key === 'Enter' && handleSearch()}
            />
          </InputGroup>

          <Button onClick={handleSearch} isDisabled={!searchTerm}>Найти</Button>
          {searchTerm && <Button variant="ghost" onClick={resetSearch}>Сбросить</Button>}
        </HStack>

        {isAdmin && (
          <Select 
            placeholder="Все клиники" 
            value={selectedClinic} 
            onChange={e => setSelectedClinic(e.target.value)}
            maxWidth={{ base: "100%", md: "200px" }}
          >
            {clinics.map(clinic => (
              <option key={clinic._id} value={clinic._id}>{clinic.name}</option>
            ))}
          </Select>
        )}

        <Select 
          placeholder="Все статусы" 
          value={statusFilter} 
          onChange={e => setStatusFilter(e.target.value)}
          maxWidth={{ base: "100%", md: "200px" }}
        >
          <option value="pending">Не оплачен</option>
          <option value="paid">Оплачен</option>
          <option value="cancelled">Отменен</option>
        </Select>

        <Button 
          variant={viewMode === 'all' ? 'solid' : 'outline'} 
          colorScheme="brand"
          onClick={() => setViewMode('all')}
        >
          Все заказы
        </Button>

        <Button 
          variant={viewMode === 'unpaid' ? 'solid' : 'outline'} 
          colorScheme="orange"
          onClick={() => setViewMode('unpaid')}
        >
          Неоплаченные
        </Button>
      </Flex>

      {loading ? (
        <Center py={8}>
          <Spinner size="lg" color="brand.500" thickness="4px" />
        </Center>
      ) : (
        <Card bg={cardBg} boxShadow="sm" borderRadius="lg" overflow="hidden">
          {filteredOrders.length > 0 ? (
            <Table variant="simple">
              <Thead>
                <Tr>
                  <Th>№ заказа</Th>
                  {isAdmin && <Th>Клиника</Th>}
                  <Th>Пациент</Th>
                  <Th>Дата</Th>
                  <Th>Срок сдачи</Th>
                  <Th isNumeric>Сумма</Th>
                  <Th>Статус</Th>
                  <Th width="120px">Действия</Th>
                </Tr>
              </Thead>
              <Tbody>
                {filteredOrders.map(order => (
                  <Tr key={order._id}>
                    <Td fontWeight="medium">#{order.orderNumber}</Td>
                    {isAdmin && <Td>{order.clinicId?.name || '-'}</Td>}
                    <Td>{order.patientName || '-'}</Td>
                    <Td>{new Date(order.createdAt).toLocaleDateString()}</Td>
                    <Td>{order.deadline ? new Date(order.deadline).toLocaleDateString() : '-'}</Td>
                    <Td isNumeric>₽{order.totalAmount.toLocaleString()}</Td>
                    <Td>
                      <Badge colorScheme={getStatusColorScheme(order.status)}>
                        {getStatusText(order.status)}
                      </Badge>
                    </Td>
                    <Td>
                      <HStack spacing={2}>
                        {isAdmin && order.status === 'pending' && (
                          <IconButton
                            icon={<FiCheck />}
                            aria-label="Отметить как оплаченный"
                            size="sm"
                            colorScheme="green"
                            variant="ghost"
                            onClick={() => handleMarkAsPaid(order._id)}
                          />
                        )}
                        <IconButton
                          icon={<FiCornerDownRight />}
                          aria-label="Детали"
                          size="sm"
                          colorScheme="blue"
                          variant="ghost"
                          onClick={() => viewOrderDetails(order)}
                        />
                      </HStack>
                    </Td>
                  </Tr>
                ))}
              </Tbody>
            </Table>
          ) : (
            <Center py={8}>
              <VStack>
                <Icon as={FiShoppingCart} fontSize="4xl" color="gray.400" />
                <Text color="gray.500">Заказы не найдены</Text>
                <Text fontSize="sm" color="gray.400">
                  {searchTerm ? 'Попробуйте изменить параметры поиска' : 'Нет заказов по заданным параметрам'}
                </Text>
              </VStack>
            </Center>
          )}
        </Card>
      )}

      {/* Order Details Modal */}
      <Modal isOpen={isOpen} onClose={onClose} size="xl">
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>
            Заказ #{orderDetails?.orderNumber}
            <Badge ml={2} colorScheme={orderDetails ? getStatusColorScheme(orderDetails.status) : 'gray'}>
              {orderDetails ? getStatusText(orderDetails.status) : ''}
            </Badge>
          </ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            {orderDetails && (
              <>
                <SimpleGrid columns={2} spacing={4} mb={6}>
                  <Box>
                    <Text fontWeight="bold">Пациент:</Text>
                    <Text>{orderDetails.patientName}</Text>
                  </Box>
                  {isAdmin && (
                    <Box>
                      <Text fontWeight="bold">Клиника:</Text>
                      <Text>{orderDetails.clinicId?.name || '-'}</Text>
                    </Box>
                  )}
                  <Box>
                    <Text fontWeight="bold">Дата создания:</Text>
                    <Text>{new Date(orderDetails.createdAt).toLocaleDateString()}</Text>
                  </Box>
                  <Box>
                    <Text fontWeight="bold">Срок сдачи:</Text>
                    <Text>{orderDetails.deadline ? new Date(orderDetails.deadline).toLocaleDateString() : '-'}</Text>
                  </Box>
                  {orderDetails.status === 'paid' && (
                    <Box>
                      <Text fontWeight="bold">Дата оплаты:</Text>
                      <Text>{orderDetails.paymentDate ? new Date(orderDetails.paymentDate).toLocaleDateString() : '-'}</Text>
                    </Box>
                  )}
                </SimpleGrid>

                <Text fontWeight="bold" mb={2}>Товары и услуги:</Text>
                <Table variant="simple" size="sm" mb={4}>
                  <Thead>
                    <Tr>
                      <Th>Название</Th>
                      <Th>Кол-во</Th>
                      <Th>Цвет</Th>
                      <Th isNumeric>Цена</Th>
                      <Th isNumeric>Сумма</Th>
                    </Tr>
                  </Thead>
                  <Tbody>
                    {orderDetails.items.map((item, index) => (
                      <Tr key={index}>
                        <Td>{item.product?.name || 'Товар не найден'}</Td>
                        <Td>{item.quantity}</Td>
                        <Td>{item.color || '-'}</Td>
                        <Td isNumeric>₽{item.price.toLocaleString()}</Td>
                        <Td isNumeric>₽{(item.price * item.quantity).toLocaleString()}</Td>
                      </Tr>
                    ))}
                  </Tbody>
                  <Tfoot>
                    <Tr>
                      <Th colSpan={4} textAlign="right">Итого:</Th>
                      <Th isNumeric>₽{orderDetails.totalAmount.toLocaleString()}</Th>
                    </Tr>
                  </Tfoot>
                </Table>

                {orderDetails.notes && (
                  <>
                    <Text fontWeight="bold">Примечания:</Text>
                    <Text>{orderDetails.notes}</Text>
                  </>
                )}
              </>
            )}
          </ModalBody>
          <ModalFooter>
            {isAdmin && orderDetails && orderDetails.status === 'pending' && (
              <Button 
                colorScheme="green" 
                mr={3}
                leftIcon={<FiCheck />}
                onClick={() => {
                  handleMarkAsPaid(orderDetails._id);
                  onClose();
                }}
              >
                Отметить как оплаченный
              </Button>
            )}
            <Button variant="ghost" onClick={onClose}>Закрыть</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </Box>
  );
};

