import React, { useEffect, useState, useContext } from 'react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import {
  Box,
  Heading,
  Text,
  SimpleGrid,
  Stat,
  StatLabel,
  StatNumber,
  Card,
  CardHeader,
  CardBody,
  Button,
  Flex,
  Icon,
  Divider,
  useColorModeValue,
  Spinner,
  Center,
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
  HStack,
  VStack,
  Avatar,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  Input,
  Select,
  Badge,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  FormControl,
  FormLabel,
  IconButton,
  InputGroup,
  InputLeftElement,
  Spacer,
  Tooltip,
  Container,
  useToast,
  Image,
  Drawer,
  DrawerBody,
  DrawerCloseButton,
  DrawerContent,
  DrawerHeader,
  DrawerOverlay,
  useBreakpointValue,
  Tfoot,
} from '@chakra-ui/react';
import { 
  FiShoppingCart, 
  FiDollarSign, 
  FiAlertCircle, 
  FiUser, 
  FiPackage, 
  FiLogOut, 
  FiSearch, 
  FiPlus, 
  FiEdit, 
  FiTrash2, 
  FiCornerDownRight, 
  FiCheck, 
  FiX,
  FiClock,
  FiEye,
  FiMessageSquare,
  FiPhone,
  FiMail,
  FiMenu,
  FiHome,
  FiSettings,
  FiBarChart2,
  FiArrowRight
} from 'react-icons/fi';
import API from '../utils/api';
import AuthContext from '../context/AuthContext';

const Dashboard = () => {
  const { user, isAdmin, logout } = useContext(AuthContext);
  const [activeTab, setActiveTab] = useState(0);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const toast = useToast();
  const { isOpen: isMobileMenuOpen, onOpen: onMobileMenuOpen, onClose: onMobileMenuClose } = useDisclosure();
  const isMobile = useBreakpointValue({ base: true, md: false });

  const handleLogout = () => {
    logout();
    navigate('/login');
    toast({
      title: "Вы вышли из системы",
      status: "info",
      duration: 3000,
      isClosable: true,
      position: "top-right"
    });
  };

  const cardBg = useColorModeValue('white', 'gray.700');
  const borderColor = useColorModeValue('gray.200', 'gray.700');
  const sidebarBg = useColorModeValue('white', 'gray.800');
  const activeBg = useColorModeValue('brand.50', 'brand.900');
  const activeColor = useColorModeValue('brand.600', 'brand.200');

  // Load data based on active tab
  useEffect(() => {
    setLoading(false);
  }, [activeTab]);

  const menuItems = isAdmin ? [
    { icon: FiHome, label: 'Панель управления', index: 0 },
    { icon: FiUser, label: 'Клиники', index: 1 },
    { icon: FiPackage, label: 'Товары', index: 2 },
    { icon: FiShoppingCart, label: 'Заказы', index: 3 },
  ] : [
    { icon: FiHome, label: 'Панель управления', index: 0 },
    { icon: FiPackage, label: 'Прайс-лист', index: 1 },
    { icon: FiShoppingCart, label: 'Заказы', index: 2 },
    { icon: FiPlus, label: 'Создать заказ', index: 3 },
  ];

  return (
    <Box minH="100vh" bg={useColorModeValue('gray.50', 'gray.900')}>
      {/* Header */}
      <Flex 
        as="header"
        bg={cardBg} 
        p={4} 
        shadow="md" 
        justify="space-between"
        align="center"
        position="sticky"
        top={0}
        zIndex={10}
        borderBottomWidth="1px" 
        borderColor={borderColor}
      >
        <Flex align="center">
          {isMobile && (
            <IconButton
              icon={<FiMenu />}
              aria-label="Open menu"
              mr={4}
              variant="ghost"
              onClick={onMobileMenuOpen}
            />
          )}
          <Heading size="lg" color="brand.500" letterSpacing="tight" fontWeight="extrabold">
            Dental Lab
          </Heading>
        </Flex>

        <HStack spacing={4}>
          <Flex align="center" color={useColorModeValue('gray.600', 'gray.300')}>
            <Icon as={FiUser} mr={2} />
            <Text fontWeight="medium">{user?.username}</Text>
          </Flex>
          <Menu>
            <MenuButton 
              as={Avatar}
              size="sm"
              bg="brand.500"
              color="white"
              cursor="pointer"
              _hover={{ transform: 'scale(1.05)', boxShadow: 'md' }}
              transition="all 0.2s"
            />
            <MenuList shadow="lg" borderRadius="xl">
              <MenuItem 
                icon={<Icon as={FiSettings} />}
              >
                Настройки
              </MenuItem>
              <MenuItem 
                icon={<Icon as={FiLogOut} />} 
                onClick={handleLogout}
              >
                Выйти
              </MenuItem>
            </MenuList>
          </Menu>
        </HStack>
      </Flex>

      <Flex flex={1}>
        {/* Sidebar (desktop) */}
        {!isMobile && (
          <Box
            as="aside"
            w="250px"
            bg={sidebarBg}
            shadow="md"
            py={6}
            px={3}
            borderRightWidth="1px"
            borderColor={borderColor}
            position="sticky"
            top="73px"
            h="calc(100vh - 73px)"
            overflowY="auto"
          >
            <VStack spacing={1} align="stretch">
              {menuItems.map((item) => (
                <Button
                  key={item.index}
                  leftIcon={<Icon as={item.icon} boxSize={5} />}
                  variant="ghost"
                  justifyContent="flex-start"
                  py={3}
                  px={4}
                  borderRadius="md"
                  fontWeight="medium"
                  onClick={() => setActiveTab(item.index)}
                  bg={activeTab === item.index ? activeBg : 'transparent'}
                  color={activeTab === item.index ? activeColor : 'inherit'}
                  _hover={{ bg: useColorModeValue('gray.100', 'gray.700') }}
                >
                  {item.label}
                </Button>
              ))}

              <Divider my={6} />

              <Button
                leftIcon={<Icon as={FiLogOut} boxSize={5} />}
                variant="ghost"
                justifyContent="flex-start"
                py={3}
                px={4}
                borderRadius="md"
                fontWeight="medium"
                onClick={handleLogout}
                color={useColorModeValue('red.500', 'red.300')}
                _hover={{ bg: useColorModeValue('red.50', 'red.900'), color: useColorModeValue('red.700', 'red.200') }}
              >
                Выйти
              </Button>
            </VStack>
          </Box>
        )}

        {/* Mobile menu */}
        <Drawer isOpen={isMobileMenuOpen} placement="left" onClose={onMobileMenuClose}>
          <DrawerOverlay />
          <DrawerContent maxW="250px">
            <DrawerHeader borderBottomWidth="1px" px={4} py={6}>
              <Flex align="center" justify="space-between">
                <Heading size="md" color="brand.500">Dental Lab</Heading>
                <DrawerCloseButton position="static" />
              </Flex>
            </DrawerHeader>
            <DrawerBody p={0}>
              <VStack spacing={1} align="stretch" py={4}>
                {menuItems.map((item) => (
                  <Button
                    key={item.index}
                    leftIcon={<Icon as={item.icon} boxSize={5} />}
                    variant="ghost"
                    justifyContent="flex-start"
                    py={3}
                    px={4}
                    borderRadius="0"
                    fontWeight="medium"
                    onClick={() => {
                      setActiveTab(item.index);
                      onMobileMenuClose();
                    }}
                    bg={activeTab === item.index ? activeBg : 'transparent'}
                    color={activeTab === item.index ? activeColor : 'inherit'}
                    _hover={{ bg: useColorModeValue('gray.100', 'gray.700') }}
                  >
                    {item.label}
                  </Button>
                ))}

                <Divider my={6} />

                <Button
                  leftIcon={<Icon as={FiLogOut} boxSize={5} />}
                  variant="ghost"
                  justifyContent="flex-start"
                  py={3}
                  px={4}
                  borderRadius="0"
                  fontWeight="medium"
                  onClick={handleLogout}
                  color={useColorModeValue('red.500', 'red.300')}
                  _hover={{ bg: useColorModeValue('red.50', 'red.900'), color: useColorModeValue('red.700', 'red.200') }}
                >
                  Выйти
                </Button>
              </VStack>
            </DrawerBody>
          </DrawerContent>
        </Drawer>

        {/* Main content */}
        <Box flex={1} p={{ base: 4, md: 6 }} maxW="100%">
          <Box maxW="1600px" mx="auto">
            {/* Conditional rendering based on active tab */}
            {activeTab === 0 && (
              <DashboardPanel isAdmin={isAdmin} setActiveTab={setActiveTab} />
            )}

            {/* Admin Panels */}
            {isAdmin ? (
              <>
                {activeTab === 1 && <ClinicsPanel />}
                {activeTab === 2 && <ProductsPanel />}
                {activeTab === 3 && <OrdersPanel isAdmin={true} />}
              </>
            ) : (
              <>
                {/* Clinic Panels */}
                {activeTab === 1 && <PriceListPanel />}
                {activeTab === 2 && <OrdersPanel isAdmin={false} />}
                {activeTab === 3 && <CreateOrderPanel setActiveTab={setActiveTab} />}
              </>
            )}
          </Box>
        </Box>
      </Flex>
    </Box>
  );
};

// Dashboard Panel Component
const DashboardPanel = ({ isAdmin, setActiveTab }) => {
  const [stats, setStats] = useState({
    totalOrders: 0,
    pendingOrders: 0,
    totalSpent: 0,
    recentOrders: [],
  });
  const [unpaidOrders, setUnpaidOrders] = useState([]);
  const [selectedClinic, setSelectedClinic] = useState('');
  const [clinics, setClinics] = useState([]);
  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const fetchStats = async () => {
      try {
        setLoading(true);
        setErrorMessage('');

        // Get all orders
        const ordersRes = await API.get('/orders');

        // Get pending orders
        const pendingRes = await API.get('/orders/unpaid');

        // Calculate summary statistics
        const totalOrders = ordersRes.data.length;
        const pendingOrders = pendingRes.data.length;

        // Calculate total amount of paid orders
        const paidOrders = ordersRes.data.filter(order => order.status === 'paid');
        const totalSpent = paidOrders.reduce((sum, order) => sum + order.totalAmount, 0);

        // Recent orders (last 5)
        const recentOrders = ordersRes.data.slice(0, 5);

        setStats({
          totalOrders,
          pendingOrders,
          totalSpent,
          recentOrders
        });

        setUnpaidOrders(pendingRes.data);

        // If admin, fetch clinics
        if (isAdmin) {
          const clinicsRes = await API.get('/clinics');
          setClinics(clinicsRes.data);
        }

        setLoading(false);
      } catch (err) {
        console.error('Error fetching stats:', err);
        setErrorMessage('Ошибка загрузки данных: ' + (err.response?.data?.message || err.message));
        setLoading(false);
      }
    };

    fetchStats();
  }, [isAdmin]);

  const handleMarkAsPaid = async (orderId) => {
    try {
      setErrorMessage('');
      await API.post(`/orders/${orderId}/pay`);

      // Update orders lists
      setUnpaidOrders(unpaidOrders.filter(order => order._id !== orderId));

      // Update stats
      setStats(prev => ({
        ...prev,
        pendingOrders: prev.pendingOrders - 1
      }));
    } catch (err) {
      console.error('Error marking order as paid:', err);
      setErrorMessage('Ошибка при оплате заказа: ' + (err.response?.data?.message || err.message));
    }
  };

  const goToAllOrders = () => {
    // Navigate to Orders tab (index 3 for admin, 1 for clinic)
    const tabIndex = isAdmin ? 3 : 1;
    setActiveTab(tabIndex);
  };

  const filteredOrders = selectedClinic 
    ? unpaidOrders.filter(order => order.clinicId._id === selectedClinic)
    : unpaidOrders;

  const cardBg = useColorModeValue('white', 'gray.700');
  const highlightBg = useColorModeValue('brand.50', 'brand.900');
  const accentColor = useColorModeValue('brand.500', 'brand.200');

  if (loading && stats.recentOrders.length === 0) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      {errorMessage && (
        <Box mb={6} p={4} borderRadius="md" bg="red.100" color="red.800">
          <Flex align="center">
            <Icon as={FiAlertCircle} mr={2} />
            <Text fontWeight="medium">{errorMessage}</Text>
          </Flex>
        </Box>
      )}

      {isAdmin ? (
        // Admin Dashboard
        <Box>
          <Flex 
            mb={8} 
            p={6} 
            borderRadius="xl" 
            bg={highlightBg} 
            color="brand.500"
            direction={{ base: 'column', md: 'row' }}
            align="center"
            justify="space-between"
            boxShadow="sm"
          >
            <Box>
              <Heading size="lg" mb={2}>Панель администратора</Heading>
              <Text fontSize="md" opacity={0.8}>Управление клиниками, товарами и заказами</Text>
            </Box>
            <HStack spacing={4} mt={{ base: 4, md: 0 }}>
              <Button 
                leftIcon={<Icon as={FiUser} />} 
                colorScheme="brand" 
                variant="outline"
                onClick={() => setActiveTab(1)}
                size="lg"
                fontWeight="normal"
              >
                Клиники
              </Button>
              <Button 
                leftIcon={<Icon as={FiPackage} />} 
                colorScheme="brand" 
                variant="outline"
                onClick={() => setActiveTab(2)}
                size="lg"
                fontWeight="normal"
              >
                Товары
              </Button>
            </HStack>
          </Flex>

          <SimpleGrid columns={{ base: 1, md: 3 }} spacing={6} mb={8}>
            <StatCard 
              title="Всего заказов" 
              value={stats.totalOrders}
              icon={FiShoppingCart}
              accentColor="brand.500"
              onClick={goToAllOrders}
            />
            <StatCard 
              title="Неоплаченных заказов" 
              value={stats.pendingOrders}
              icon={FiAlertCircle}
              accentColor="orange.500"
              onClick={goToAllOrders}
            />
            <StatCard 
              title="Общая сумма оплаченных" 
              value={`₽${stats.totalSpent.toLocaleString()}`}
              icon={FiDollarSign}
              accentColor="green.500"
            />
          </SimpleGrid>

          <Card 
            bg={cardBg} 
            boxShadow="sm" 
            borderRadius="xl" 
            overflow="hidden"
            borderWidth="1px"
            borderColor={useColorModeValue('gray.200', 'gray.700')}
          >
            <CardHeader pb={2} bg={useColorModeValue('gray.50', 'gray.800')} borderBottomWidth="1px">
              <Flex justify="space-between" align="center">
                <Heading size="md">
                  <Flex align="center">
                    <Icon as={FiAlertCircle} color="orange.500" mr={2} />
                    Неоплаченные заказы
                  </Flex>
                </Heading>

                <Select 
                  placeholder="Все клиники" 
                  value={selectedClinic} 
                  onChange={e => setSelectedClinic(e.target.value)}
                  width="250px"
                  bg={cardBg}
                  icon={<Icon as={FiUser} color="gray.500" />}
                >
                  {clinics.map(clinic => (
                    <option key={clinic._id} value={clinic._id}>{clinic.name}</option>
                  ))}
                </Select>
              </Flex>
            </CardHeader>
            <CardBody p={0}>
              {loading ? (
                <Center py={8}>
                  <Spinner size="lg" color="brand.500" thickness="4px" />
                </Center>
              ) : filteredOrders.length > 0 ? (
                <Table variant="simple">
                  <Thead bg={useColorModeValue('gray.50', 'gray.800')}>
                    <Tr>
                      <Th>№ заказа</Th>
                      <Th>Клиника</Th>
                      <Th>Пациент</Th>
                      <Th>Дата</Th>
                      <Th>Срок сдачи</Th>
                      <Th isNumeric>Сумма</Th>
                      <Th width="100px">Действия</Th>
                    </Tr>
                  </Thead>
                  <Tbody>
                    {filteredOrders.map((order, index) => (
                      <Tr 
                        key={order._id} 
                        bg={index % 2 === 0 ? 'transparent' : useColorModeValue('gray.50', 'gray.800')}
                        _hover={{ bg: useColorModeValue('gray.100', 'gray.700') }}
                        transition="background 0.2s"
                      >
                        <Td fontWeight="medium">#{order.orderNumber}</Td>
                        <Td>{order.clinicId?.name || '-'}</Td>
                        <Td>{order.patientName || '-'}</Td>
                        <Td>{new Date(order.createdAt).toLocaleDateString()}</Td>
                        <Td>{order.deadline ? new Date(order.deadline).toLocaleDateString() : '-'}</Td>
                        <Td isNumeric fontWeight="bold">₽{order.totalAmount.toLocaleString()}</Td>
                        <Td>
                          <Button 
                            size="sm" 
                            colorScheme="green"
                            leftIcon={<FiCheck />}
                            onClick={() => handleMarkAsPaid(order._id)}
                            boxShadow="sm"
                            _hover={{ transform: 'translateY(-1px)', boxShadow: 'md' }}
                          >
                            Оплачен
                          </Button>
                        </Td>
                      </Tr>
                    ))}
                  </Tbody>
                </Table>
              ) : (
                <Center py={10}>
                  <VStack spacing={3}>
                    <Box
                      p={3}
                      borderRadius="full"
                      bg={useColorModeValue('green.50', 'green.900')}
                      color={useColorModeValue('green.500', 'green.200')}
                    >
                      <Icon as={FiCheck} fontSize="4xl" />
                    </Box>
                    <Text fontSize="lg" fontWeight="medium" color="gray.500">Все заказы оплачены</Text>
                    <Text fontSize="sm" color="gray.400">
                      На данный момент нет неоплаченных заказов
                    </Text>
                  </VStack>
                </Center>
              )}

              {filteredOrders.length > 0 && (
                <Flex 
                  justifyContent="center" 
                  py={4}
                  borderTopWidth="1px"
                  borderColor={useColorModeValue('gray.200', 'gray.700')}
                >
                  <Button 
                    colorScheme="brand" 
                    variant="outline"
                    onClick={goToAllOrders}
                    leftIcon={<FiShoppingCart />}
                    boxShadow="sm"
                    _hover={{ transform: 'translateY(-1px)', boxShadow: 'md' }}
                  >
                    Все заказы
                  </Button>
                </Flex>
              )}
            </CardBody>
          </Card>
        </Box>
      ) : (
        // Clinic dashboard
        <Card bg={cardBg} boxShadow="sm" borderRadius="lg">
          <CardHeader pb={2}>
            <Heading size="md">Последние заказы</Heading>
          </CardHeader>
          <CardBody>
            {loading ? (
              <Center py={8}>
                <Spinner size="lg" color="brand.500" thickness="4px" />
              </Center>
            ) : stats.recentOrders.length > 0 ? (
              <Table variant="simple">
                <Thead>
                  <Tr>
                    <Th>№ заказа</Th>
                    <Th>Пациент</Th>
                    <Th>Дата</Th>
                    <Th isNumeric>Сумма</Th>
                    <Th>Статус</Th>
                  </Tr>
                </Thead>
                <Tbody>
                  {stats.recentOrders.map(order => (
                    <Tr key={order._id}>
                      <Td fontWeight="medium">#{order.orderNumber}</Td>
                      <Td>{order.patientName || '-'}</Td>
                      <Td>{new Date(order.createdAt).toLocaleDateString()}</Td>
                      <Td isNumeric>₽{order.totalAmount.toLocaleString()}</Td>
                      <Td>
                        <Badge colorScheme={getStatusColorScheme(order.status)}>
                          {getStatusText(order.status)}
                        </Badge>
                      </Td>
                    </Tr>
                  ))}
                </Tbody>
              </Table>
            ) : (
              <Center py={8}>
                <Text color="gray.500">Заказы не найдены</Text>
              </Center>
            )}

            <HStack spacing={4} justifyContent="center" mt={4}>
              <Button 
                colorScheme="brand" 
                variant="outline"
                onClick={goToAllOrders}
                leftIcon={<FiShoppingCart />}
              >
                Все заказы
              </Button>

              <Button 
                colorScheme="brand"
                onClick={() => setActiveTab(2)} // Navigate to Create Order tab
                leftIcon={<FiPlus />}
              >
                Создать заказ
              </Button>
            </HStack>
          </CardBody>
        </Card>
      )}
    </Box>
  );
};

