import React, { useEffect, useState, useContext } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Box,
  Heading,
  Text,
  SimpleGrid,
  Stat,
  StatLabel,
  StatNumber,
  StatHelpText,
  Card,
  CardHeader,
  CardBody,
  Button,
  Flex,
  Icon,
  Divider,
  useColorModeValue,
  Spinner,
  Center,
} from '@chakra-ui/react';
import { FiShoppingCart, FiDollarSign, FiAlertCircle, FiClock } from 'react-icons/fi';
import axios from 'axios';
import AuthContext from '../context/AuthContext';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalOrders: 0,
    pendingOrders: 0,
    totalSpent: 0,
    recentOrders: [],
  });
  const [loading, setLoading] = useState(true);
  const { user } = useContext(AuthContext);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        // Get all orders
        const ordersRes = await axios.get('/api/orders');

        // Get pending orders
        const pendingRes = await axios.get('/api/orders/unpaid');

        // Calculate total spent (for paid orders only)
        const totalSpent = ordersRes.data
          .filter(order => order.status === 'paid')
          .reduce((acc, order) => acc + order.totalAmount, 0);

        setStats({
          totalOrders: ordersRes.data.length,
          pendingOrders: pendingRes.data.length,
          totalSpent,
          recentOrders: ordersRes.data.slice(0, 5), // Get 5 most recent orders
        });

        setLoading(false);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const cardBg = useColorModeValue('white', 'gray.700');
  const textColor = useColorModeValue('gray.600', 'gray.200');

  if (loading) {
    return (
      <Center h="calc(100vh - 100px)">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Flex justify="space-between" align="center" mb={6}>
        <Box>
          <Heading size="lg" mb={1}>Панель управления</Heading>
          <Text color={textColor}>Добро пожаловать, {user?.username}</Text>
        </Box>
        <Button
          as={RouterLink}
          to="/orders/create"
          colorScheme="brand"
          leftIcon={<Icon as={FiShoppingCart} />}
        >
          Создать заказ
        </Button>
      </Flex>

      <SimpleGrid columns={{ base: 1, md: 3 }} spacing={6} mb={6}>
        <StatCard
          title="Всего заказов"
          value={stats.totalOrders}
          icon={FiShoppingCart}
          accentColor="brand.500"
        />
        <StatCard
          title="Неоплаченные заказы"
          value={stats.pendingOrders}
          icon={FiAlertCircle}
          accentColor="orange.500"
        />
        <StatCard
          title="Сумма оплаченных заказов"
          value={`₽${stats.totalSpent.toLocaleString()}`}
          icon={FiDollarSign}
          accentColor="green.500"
        />
      </SimpleGrid>

      <Card bg={cardBg} boxShadow="sm" borderRadius="lg">
        <CardHeader>
          <Heading size="md">Последние заказы</Heading>
        </CardHeader>
        <CardBody>
          {stats.recentOrders.length > 0 ? (
            stats.recentOrders.map((order, index) => (
              <React.Fragment key={order._id}>
                <Flex justify="space-between" align="center" py={2}>
                  <Box>
                    <Text fontWeight="bold">Заказ #{order.orderNumber}</Text>
                    <Text fontSize="sm" color={textColor}>
                      {new Date(order.createdAt).toLocaleDateString()} • {order.items.length} {getPluralForm(order.items.length)}
                    </Text>
                  </Box>
                  <Box>
                    <Text fontWeight="bold" color={getStatusColor(order.status)}>
                      {getStatusText(order.status)}
                    </Text>
                    <Text fontWeight="medium" fontSize="md">
                      ₽{order.totalAmount.toLocaleString()}
                    </Text>
                  </Box>
                  <Button
                    as={RouterLink}
                    to={`/orders/${order._id}`}
                    size="sm"
                    colorScheme="brand"
                    variant="outline"
                  >
                    Подробнее
                  </Button>
                </Flex>
                {index < stats.recentOrders.length - 1 && <Divider />}
              </React.Fragment>
            ))
          ) : (
            <Text>Нет заказов</Text>
          )}

          {stats.totalOrders > 5 && (
            <Box mt={4} textAlign="center">
              <Button
                as={RouterLink}
                to="/orders"
                colorScheme="brand"
                variant="ghost"
                size="sm"
              >
                Посмотреть все заказы
              </Button>
            </Box>
          )}
        </CardBody>
      </Card>
    </Box>
  );
};

const StatCard = ({ title, value, icon, accentColor }) => {
  const cardBg = useColorModeValue('white', 'gray.700');

  return (
    <Card bg={cardBg} boxShadow="sm" borderRadius="lg">
      <CardBody>
        <Flex justify="space-between">
          <Box>
            <Stat>
              <StatLabel fontSize="sm" fontWeight="medium" isTruncated>
                {title}
              </StatLabel>
              <StatNumber fontSize="3xl" fontWeight="bold">
                {value}
              </StatNumber>
              <StatHelpText></StatHelpText>
            </Stat>
          </Box>
          <Flex
            flexShrink={0}
            alignItems="center"
            justifyContent="center"
            borderRadius="full"
            bg={`${accentColor}10`}
            color={accentColor}
            boxSize={12}
          >
            <Icon as={icon} boxSize={6} />
          </Flex>
        </Flex>
      </CardBody>
    </Card>
  );
};

// Helper functions
const getStatusText = (status) => {
  switch (status) {
    case 'pending': return 'Не оплачен';
    case 'paid': return 'Оплачен';
    case 'cancelled': return 'Отменен';
    default: return status;
  }
};

const getStatusColor = (status) => {
  switch (status) {
    case 'pending': return 'orange.500';
    case 'paid': return 'green.500';
    case 'cancelled': return 'red.500';
    default: return 'gray.500';
  }
};

const getPluralForm = (count) => {
  // Russian plural forms for "item"
  if (count % 10 === 1 && count % 100 !== 11) {
    return 'товар';
  } else if ([2, 3, 4].includes(count % 10) && ![12, 13, 14].includes(count % 100)) {
    return 'товара';
  } else {
    return 'товаров';
  }
};

export default Dashboard;
