  );
};

// Orders Panel Component
const OrdersPanel = ({ isAdmin }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [clinics, setClinics] = useState([]);
  const [selectedClinic, setSelectedClinic] = useState('');
  const [statusFilter, setStatusFilter] = useState('');

  useEffect(() => {
    fetchOrders();

    // Если это админ, загрузить список клиник
    if (isAdmin) {
      fetchClinics();
    }
  }, [isAdmin, selectedClinic, statusFilter]);

  const fetchOrders = async () => {
    try {
      let url = '/orders';
      const params = {};

      // Добавляем параметры фильтрации
      if (selectedClinic && isAdmin) {
        params.clinicId = selectedClinic;
      }

      if (statusFilter) {
        params.status = statusFilter;
      }

      // Если есть параметры, добавляем их к URL
      if (Object.keys(params).length > 0) {
        const queryString = new URLSearchParams(params).toString();
        url = `${url}?${queryString}`;
      }

      const res = await API.get(url);
      setOrders(res.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching orders:', err);
      setLoading(false);
    }
  };

  const fetchClinics = async () => {
    try {
      const res = await API.get('/clinics');
      setClinics(res.data);
    } catch (err) {
      console.error('Error fetching clinics:', err);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!searchTerm.trim()) {
      return fetchOrders();
    }

    try {
      setLoading(true);
      const res = await API.get(`/orders/search?term=${searchTerm}`);
      setOrders(res.data);
      setLoading(false);
    } catch (err) {
      console.error('Error searching orders:', err);
      setLoading(false);
    }
  };

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      if (newStatus === 'paid') {
        await API.post(`/orders/${orderId}/pay`);
      } else if (newStatus === 'cancelled') {
        await API.post(`/orders/${orderId}/cancel`);
      }

      // Обновляем список заказов
      fetchOrders();
    } catch (err) {
      console.error('Error updating order status:', err);
    }
  };

  const filteredOrders = searchTerm && !loading
    ? orders.filter(order => 
        order.orderNumber.toLowerCase().includes(searchTerm.toLowerCase()))
    : orders;

  const getStatusBadge = (status) => {
    switch (status) {
      case 'pending':
        return <Badge colorScheme="yellow">Ожидает оплаты</Badge>;
      case 'paid':
        return <Badge colorScheme="green">Оплачен</Badge>;
      case 'cancelled':
        return <Badge colorScheme="red">Отменен</Badge>;
      default:
        return <Badge>Неизвестно</Badge>;
    }
  };

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Heading size="md" mb={6}>
        {isAdmin ? 'Все заказы' : 'Ваши заказы'}
      </Heading>

      <Flex mb={6} direction={{ base: 'column', md: 'row' }} gap={4}>
        <HStack flex="1">
          <form onSubmit={handleSearch} style={{ width: '100%' }}>
            <InputGroup>
              <InputLeftElement pointerEvents="none">
                <Icon as={FiSearch} color="gray.400" />
              </InputLeftElement>
              <Input
                placeholder="Поиск по номеру заказа"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                bg="white"
                borderRadius="md"
              />
            </InputGroup>
          </form>
        </HStack>

        {isAdmin && (
          <Select
            placeholder="Выберите клинику"
            value={selectedClinic}
            onChange={(e) => setSelectedClinic(e.target.value)}
            bg="white"
            width={{ base: '100%', md: '250px' }}
          >
            <option value="">Все клиники</option>
            {clinics.map((clinic) => (
              <option key={clinic._id} value={clinic._id}>
                {clinic.name}
              </option>
            ))}
          </Select>
        )}

        <Select
          placeholder="Статус заказа"
          value={statusFilter}
          onChange={(e) => setStatusFilter(e.target.value)}
          bg="white"
          width={{ base: '100%', md: '200px' }}
        >
          <option value="">Все статусы</option>
          <option value="pending">Ожидает оплаты</option>
          <option value="paid">Оплачен</option>
          <option value="cancelled">Отменен</option>
        </Select>
      </Flex>

      {filteredOrders.length > 0 ? (
        <TableContainer
          border="1px"
          borderColor={borderColor}
          borderRadius="lg"
          boxShadow="sm"
        >
          <Table variant="simple" bg={cardBg}>
            <Thead>
              <Tr>
                <Th>№ заказа</Th>
                {isAdmin && <Th>Клиника</Th>}
                <Th>Дата</Th>
                <Th isNumeric>Сумма</Th>
                <Th>Статус</Th>
                {isAdmin && <Th>Действия</Th>}
              </Tr>
            </Thead>
            <Tbody>
              {filteredOrders.map((order) => (
                <Tr key={order._id}>
                  <Td fontWeight="bold">{order.orderNumber}</Td>
                  {isAdmin && (
                    <Td>{order.clinicId?.name || 'Неизвестно'}</Td>
                  )}
                  <Td>{new Date(order.createdAt).toLocaleDateString()}</Td>
                  <Td isNumeric fontWeight="medium">
                    ₽{order.totalAmount?.toLocaleString() || 0}
                  </Td>
                  <Td>{getStatusBadge(order.status)}</Td>
                  {isAdmin && (
                    <Td>
                      <Menu>
                        <MenuButton
                          as={Button}
                          size="sm"
                          rightIcon={<FiCornerDownRight />}
                          variant="outline"
                        >
                          Действия
                        </MenuButton>
                        <MenuList>
                          {order.status === 'pending' && (
                            <>
                              <MenuItem
                                icon={<FiCheck />}
                                onClick={() => handleStatusChange(order._id, 'paid')}
                              >
                                Отметить как оплаченный
                              </MenuItem>
                              <MenuItem
                                icon={<FiTrash2 />}
                                onClick={() => handleStatusChange(order._id, 'cancelled')}
                              >
                                Отменить заказ
                              </MenuItem>
                            </>
                          )}
                        </MenuList>
                      </Menu>
                    </Td>
                  )}
                </Tr>
              ))}
            </Tbody>
          </Table>
        </TableContainer>
      ) : (
        <Box
          p={8}
          bg={cardBg}
          borderRadius="lg"
          textAlign="center"
          border="1px"
          borderColor={borderColor}
        >
          <Icon as={FiAlertCircle} boxSize={10} color="gray.400" mb={4} />
          <Heading size="md" mb={2}>
            Заказы не найдены
          </Heading>
          <Text color="gray.500">
            {searchTerm
              ? 'Не найдено заказов по вашему запросу'
              : isAdmin
              ? 'В системе пока нет заказов'
              : 'У вас пока нет заказов'}
          </Text>
        </Box>
      )}
    </Box>
  );
};

