// Create Order Panel Component (Clinic only)
const CreateOrderPanel = () => {
  const [prices, setPrices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [cartItems, setCartItems] = useState([]);
  const [notes, setNotes] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchPrices();
  }, []);

  const fetchPrices = async () => {
    try {
      const res = await API.get('/products');
      setPrices(res.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching prices:', err);
      setLoading(false);
    }
  };

  const addItem = (price) => {
    // Проверяем, есть ли товар уже в корзине
    const existingItem = cartItems.find(item => item._id === price._id);

    if (existingItem) {
      // Если товар уже в корзине, увеличиваем количество
      const updatedItems = cartItems.map(item => 
        item._id === price._id 
          ? { ...item, quantity: item.quantity + 1 } 
          : item
      );
      setCartItems(updatedItems);
    } else {
      // Если товара нет в корзине, добавляем с количеством 1
      setCartItems([...cartItems, { ...price, quantity: 1 }]);
    }
  };

  const removeItem = (priceId) => {
    setCartItems(cartItems.filter(item => item._id !== priceId));
  };

  const updateQuantity = (priceId, quantity) => {
    if (quantity < 1) return;

    setCartItems(cartItems.map(item => 
      item._id === priceId ? { ...item, quantity } : item
    ));
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => {
      return total + (item.quantity * (item.price || 0));
    }, 0);
  };

  const handleSubmitOrder = async () => {
    if (cartItems.length === 0) {
      setErrorMessage('Пожалуйста, добавьте товары в заказ');
      return;
    }

    try {
      setSubmitting(true);
      setErrorMessage('');

      // Подготовка данных для отправки
      const orderData = {
        items: cartItems.map(item => ({
          productId: item._id,
          quantity: item.quantity
        })),
        notes
      };

      // Отправка запроса на создание заказа
      const res = await API.post('/orders', orderData);

      // Очищаем корзину и показываем сообщение об успехе
      setCartItems([]);
      setNotes('');
      setSuccessMessage(`Заказ №${res.data.orderNumber} успешно создан!`);

      // Через 3 секунды перенаправляем на страницу заказов
      setTimeout(() => {
        setSuccessMessage('');
        navigate('/dashboard');
      }, 3000);

    } catch (err) {
      console.error('Error creating order:', err);
      setErrorMessage(err.response?.data?.message || 'Ошибка создания заказа');
    } finally {
      setSubmitting(false);
    }
  };

  const filteredPrices = prices.filter(price => {
    if (!searchTerm) return true;

    const searchLower = searchTerm.toLowerCase();
    return (
      price.name.toLowerCase().includes(searchLower) ||
      (price.code && price.code.toLowerCase().includes(searchLower)) ||
      (price.description && price.description.toLowerCase().includes(searchLower))
    );
  });

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Heading size="md" mb={6}>Создание нового заказа</Heading>

      {successMessage && (
        <Alert status="success" mb={6} borderRadius="md">
          <AlertIcon />
          {successMessage}
        </Alert>
      )}

      {errorMessage && (
        <Alert status="error" mb={6} borderRadius="md">
          <AlertIcon />
          {errorMessage}
        </Alert>
      )}

      <SimpleGrid columns={{ base: 1, lg: 2 }} spacing={6}>
        {/* Товары */}
        <Box>
          <Card borderRadius="lg" overflow="hidden" boxShadow="sm">
            <CardHeader bg="brand.50" py={3}>
              <Heading size="sm">Доступные товары и услуги</Heading>
            </CardHeader>
            <CardBody p={4}>
              <InputGroup mb={4}>
                <InputLeftElement pointerEvents="none">
                  <Icon as={FiSearch} color="gray.400" />
                </InputLeftElement>
                <Input
                  placeholder="Поиск по названию, коду или описанию"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  bg="white"
                  borderRadius="md"
                />
              </InputGroup>

              <Box maxH="500px" overflowY="auto" pr={2}>
                {filteredPrices.length > 0 ? (
                  <VStack align="stretch" spacing={2}>
                    {filteredPrices.map(price => (
                      <Flex
                        key={price._id}
                        p={3}
                        bg="white"
                        borderRadius="md"
                        border="1px"
                        borderColor="gray.200"
                        justify="space-between"
                        align="center"
                      >
                        <Box>
                          <Text fontWeight="medium">{price.name}</Text>
                          {price.code && (
                            <Text fontSize="xs" color="gray.500">Код: {price.code}</Text>
                          )}
                        </Box>
                        <Flex align="center">
                          <Text fontWeight="bold" mr={3}>
                            {price.price !== null
                              ? `₽${price.price.toLocaleString()}`
                              : <Badge colorScheme="red">Нет цены</Badge>
                            }
                          </Text>
                          <Button
