import React, { useEffect, useState } from 'react';
import {
  Box,
  Heading,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  Input,
  InputGroup,
  InputLeftElement,
  Icon,
  Card,
  useColorModeValue,
  Center,
  Spinner,
  Container
} from '@chakra-ui/react';
import { FiSearch } from 'react-icons/fi';
import API from '../utils/api';

const PriceList = () => {
  const [prices, setPrices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchPrices = async () => {
      try {
        const res = await API.get('/prices');
        setPrices(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching prices:', err);
        setLoading(false);
      }
    };

    fetchPrices();
  }, []);

  const filteredPrices = prices.filter(
    price => price.product && price.product.name && 
    price.product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const cardBg = useColorModeValue('white', 'gray.700');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Container maxW="container.lg" py={6}>
      <Heading size="lg" mb={6}>Прайс-лист</Heading>

      <InputGroup mb={4}>
        <InputLeftElement pointerEvents="none">
          <Icon as={FiSearch} color="gray.400" />
        </InputLeftElement>
        <Input
          placeholder="Поиск товара..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </InputGroup>

      <Card bg={cardBg} boxShadow="sm" borderRadius="lg" overflow="hidden">
        <Table variant="simple">
          <Thead>
            <Tr>
              <Th>Название</Th>
              <Th isNumeric>Цена</Th>
            </Tr>
          </Thead>
          <Tbody>
            {filteredPrices.map(price => (
              <Tr key={price._id}>
                <Td fontWeight="medium">{price.product.name}</Td>
                <Td isNumeric fontWeight="bold">₽{price.price.toLocaleString()}</Td>
              </Tr>
            ))}
          </Tbody>
        </Table>
      </Card>
    </Container>
  );
};

export default PriceList;
