import React, { useEffect, useState, useContext } from 'react';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import {
  Box,
  Heading,
  Text,
  SimpleGrid,
  Stat,
  StatLabel,
  StatNumber,
  Card,
  CardHeader,
  CardBody,
  Button,
  Flex,
  Icon,
  Divider,
  useColorModeValue,
  Spinner,
  Center,
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
  HStack,
  VStack,
  Avatar,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  Input,
  Select,
  Badge,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  FormControl,
  FormLabel,
  IconButton,
  InputGroup,
  InputLeftElement,
  Textarea,
  AlertDialog,
  AlertDialogOverlay,
  AlertDialogContent,
  AlertDialogHeader,
  AlertDialogBody,
  AlertDialogFooter,
  useToast,
  Stack,
} from '@chakra-ui/react';
import { FiShoppingCart, FiDollarSign, FiAlertCircle, FiUser, FiPackage, FiLogOut, FiSearch, FiPlus, FiEdit, FiTrash2, FiCornerDownRight, FiCheck } from 'react-icons/fi';
import API from '../utils/api';
import AuthContext from '../context/AuthContext';

const Dashboard = () => {
  const { user, isAdmin, logout } = useContext(AuthContext);
  const [activeTab, setActiveTab] = useState(0);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const cardBg = useColorModeValue('white', 'gray.700');
  const borderColor = useColorModeValue('gray.200', 'gray.700');

  // Load data based on active tab
  useEffect(() => {
    setLoading(false);
  }, [activeTab]);

  return (
    <Box>
      {/* Header */}
      <Flex 
        bg={cardBg} 
        p={4} 
        shadow="sm" 
        borderBottomWidth="1px" 
        borderColor={borderColor}
        justify="space-between"
        align="center"
        position="sticky"
        top={0}
        zIndex={10}
      >
        <Heading size="lg">Clinic Price Manager</Heading>

        <HStack spacing={4}>
          <Text>{user?.username}</Text>
          <Menu>
            <MenuButton 
              as={Avatar}
              size="sm"
              bg="brand.500"
              color="white"
              cursor="pointer"
            />
            <MenuList>
              <MenuItem 
                icon={<Icon as={FiLogOut} />} 
                onClick={handleLogout}
              >
                Выйти
              </MenuItem>
            </MenuList>
          </Menu>
        </HStack>
      </Flex>

      <Box p={4}>
        <Tabs 
          colorScheme="brand" 
          variant="enclosed" 
          onChange={(index) => setActiveTab(index)}
          isLazy
        >
          <TabList mb={4}>
            <Tab><Icon as={FiUser} mr={2} />Панель управления</Tab>

            {isAdmin ? (
              <>
                <Tab><Icon as={FiUser} mr={2} />Клиники</Tab>
                <Tab><Icon as={FiPackage} mr={2} />Товары</Tab>
                <Tab><Icon as={FiShoppingCart} mr={2} />Заказы</Tab>
              </>
            ) : (
              <>
                <Tab><Icon as={FiPackage} mr={2} />Прайс-лист</Tab>
                <Tab><Icon as={FiShoppingCart} mr={2} />Заказы</Tab>
                <Tab><Icon as={FiPlus} mr={2} />Создать заказ</Tab>
              </>
            )}
          </TabList>

          <TabPanels>
            {/* Dashboard Panel */}
            <TabPanel p={0}>
              <DashboardPanel isAdmin={isAdmin} />
            </TabPanel>

            {/* Admin Panels */}
            {isAdmin ? (
              <>
                <TabPanel p={0}>
                  <ClinicsPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <ProductsPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <OrdersPanel isAdmin={true} />
                </TabPanel>
              </>
            ) : (
              <>
                {/* Clinic Panels */}
                <TabPanel p={0}>
                  <PriceListPanel />
                </TabPanel>
                <TabPanel p={0}>
                  <OrdersPanel isAdmin={false} />
                </TabPanel>
                <TabPanel p={0}>
                  <CreateOrderPanel />
                </TabPanel>
              </>
            )}
          </TabPanels>
        </Tabs>
      </Box>
    </Box>
  );
};

// Dashboard Panel Component
const DashboardPanel = ({ isAdmin }) => {
  const [stats, setStats] = useState({
    totalOrders: 0,
    pendingOrders: 0,
    totalSpent: 0,
    recentOrders: [],
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        // Get all orders
        const ordersRes = await API.get('/orders');

        // Get pending orders
        const pendingRes = await API.get('/orders/unpaid');

        // Calculate total spent (for paid orders only)
        const totalSpent = ordersRes.data
          .filter(order => order.status === 'paid')
          .reduce((acc, order) => acc + order.totalAmount, 0);

        setStats({
          totalOrders: ordersRes.data.length,
          pendingOrders: pendingRes.data.length,
          totalSpent,
          recentOrders: ordersRes.data.slice(0, 5), // Get 5 most recent orders
        });

        setLoading(false);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const cardBg = useColorModeValue('white', 'gray.700');
  const textColor = useColorModeValue('gray.600', 'gray.200');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Heading size="md" mb={6}>
        {isAdmin ? 'Статистика системы' : 'Статистика клиники'}
      </Heading>

      <SimpleGrid columns={{ base: 1, md: 3 }} spacing={6} mb={6}>
        <StatCard
          title="Всего заказов"
          value={stats.totalOrders}
          icon={FiShoppingCart}
          accentColor="brand.500"
        />
        <StatCard
          title="Неоплаченные заказы"
          value={stats.pendingOrders}
          icon={FiAlertCircle}
          accentColor="orange.500"
        />
        <StatCard
          title="Сумма оплаченных заказов"
          value={`₽${stats.totalSpent.toLocaleString()}`}
          icon={FiDollarSign}
          accentColor="green.500"
        />
      </SimpleGrid>

      <Card bg={cardBg} boxShadow="sm" borderRadius="lg">
        <CardHeader>
          <Heading size="md">Последние заказы</Heading>
        </CardHeader>
        <CardBody>
          {stats.recentOrders.length > 0 ? (
            stats.recentOrders.map((order, index) => (
              <React.Fragment key={order._id}>
                <Flex justify="space-between" align="center" py={2}>
                  <Box>
                    <Text fontWeight="bold">Заказ #{order.orderNumber}</Text>
                    <Text fontSize="sm" color={textColor}>
                      {new Date(order.createdAt).toLocaleDateString()} • {order.items.length} {getPluralForm(order.items.length)}
                    </Text>
                  </Box>
                  <Box>
                    <Badge colorScheme={getStatusColorScheme(order.status)}>
                      {getStatusText(order.status)}
                    </Badge>
                    <Text fontWeight="medium" fontSize="md">
                      ₽{order.totalAmount.toLocaleString()}
                    </Text>
                  </Box>
                </Flex>
                {index < stats.recentOrders.length - 1 && <Divider />}
              </React.Fragment>
            ))
          ) : (
            <Text>Нет заказов</Text>
          )}
        </CardBody>
      </Card>
    </Box>
  );
};

// Clinics Panel Component (Admin only)
const ClinicsPanel = () => {
  const [clinics, setClinics] = useState([]);
  const [loading, setLoading] = useState(true);
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchClinics = async () => {
      try {
        const res = await API.get('/clinics');
        setClinics(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching clinics:', err);
        setLoading(false);
      }
    };

    fetchClinics();
  }, []);

  const filteredClinics = clinics.filter(
    clinic => clinic.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const cardBg = useColorModeValue('white', 'gray.700');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Flex justify="space-between" align="center" mb={4}>
        <Heading size="md">Управление клиниками</Heading>
        <Button leftIcon={<FiPlus />} colorScheme="brand" onClick={onOpen}>
          Добавить клинику
        </Button>
      </Flex>

      <InputGroup mb={4}>
        <InputLeftElement pointerEvents="none">
          <Icon as={FiSearch} color="gray.400" />
        </InputLeftElement>
        <Input
          placeholder="Поиск клиники..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </InputGroup>

      <Card bg={cardBg} boxShadow="sm" borderRadius="lg" overflow="hidden">
        <Table variant="simple">
          <Thead>
            <Tr>
              <Th>Название</Th>
              <Th>Контакты</Th>
              <Th width="120px">Действия</Th>
            </Tr>
          </Thead>
          <Tbody>
            {filteredClinics.map(clinic => (
              <Tr key={clinic._id}>
                <Td fontWeight="medium">{clinic.name}</Td>
                <Td>
                  <Text>{clinic.contact?.phone || '-'}</Text>
                  <Text fontSize="sm" color="gray.500">{clinic.contact?.email || '-'}</Text>
                </Td>
                <Td>
                  <HStack spacing={2}>
                    <IconButton
                      icon={<FiEdit />}
                      aria-label="Редактировать"
                      size="sm"
                      colorScheme="blue"
                      variant="ghost"
                    />
                    <IconButton
                      icon={<FiTrash2 />}
                      aria-label="Удалить"
                      size="sm"
                      colorScheme="red"
                      variant="ghost"
                    />
                  </HStack>
                </Td>
              </Tr>
            ))}
          </Tbody>
        </Table>
      </Card>

      {/* Add Clinic Modal */}
      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Добавить клинику</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <VStack spacing={4}>
              <FormControl isRequired>
                <FormLabel>Название клиники</FormLabel>
                <Input placeholder="Введите название" />
              </FormControl>
              <FormControl>
                <FormLabel>Телефон</FormLabel>
                <Input placeholder="Введите телефон" />
              </FormControl>
              <FormControl>
                <FormLabel>Email</FormLabel>
                <Input placeholder="Введите email" />
              </FormControl>
              <FormControl isRequired>
                <FormLabel>Имя пользователя</FormLabel>
                <Input placeholder="Введите имя пользователя" />
              </FormControl>
              <FormControl isRequired>
                <FormLabel>Пароль</FormLabel>
                <Input type="password" placeholder="Введите пароль" />
              </FormControl>
            </VStack>
          </ModalBody>
          <ModalFooter>
            <Button variant="outline" mr={3} onClick={onClose}>
              Отмена
            </Button>
            <Button colorScheme="brand">Сохранить</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </Box>
  );
};

// Products Panel Component (Admin only)
const ProductsPanel = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [isDeleteDialogOpen, setIsDeleteDialogOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({ name: '', description: '', active: true });
  const [editedProduct, setEditedProduct] = useState(null);
  const [submitLoading, setSubmitLoading] = useState(false);
  const toast = useToast();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const res = await API.get('/products');
      setProducts(res.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching products:', err);
      toast({
        title: 'Ошибка загрузки товаров',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
      setLoading(false);
    }
  };

  const handleAddProduct = async () => {
    try {
      setSubmitLoading(true);
      await API.post('/products', newProduct);
      setIsAddModalOpen(false);
      setNewProduct({ name: '', description: '', active: true });
      fetchProducts();
      toast({
        title: 'Товар успешно добавлен',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (err) {
      console.error('Error adding product:', err);
      toast({
        title: 'Ошибка при добавлении товара',
        description: err.response?.data?.message || 'Что-то пошло не так',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setSubmitLoading(false);
    }
  };

  const handleEditProduct = async () => {
    try {
      setSubmitLoading(true);
      await API.put(`/products/${editedProduct._id}`, editedProduct);
      setIsEditModalOpen(false);
      setEditedProduct(null);
      fetchProducts();
      toast({
        title: 'Товар успешно обновлен',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (err) {
      console.error('Error updating product:', err);
      toast({
        title: 'Ошибка при обновлении товара',
        description: err.response?.data?.message || 'Что-то пошло не так',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setSubmitLoading(false);
    }
  };

  const handleDeleteProduct = async () => {
    try {
      setSubmitLoading(true);
      await API.delete(`/products/${selectedProduct._id}`);
      setIsDeleteDialogOpen(false);
      setSelectedProduct(null);
      fetchProducts();
      toast({
        title: 'Товар успешно удален',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (err) {
      console.error('Error deleting product:', err);
      toast({
        title: 'Ошибка при удалении товара',
        description: err.response?.data?.message || 'Что-то пошло не так',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setSubmitLoading(false);
    }
  };

  const filteredProducts = products.filter(
    product => product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const cardBg = useColorModeValue('white', 'gray.700');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Flex justify="space-between" align="center" mb={4}>
        <Heading size="md">Управление товарами</Heading>
        <Button 
          leftIcon={<FiPlus />} 
          colorScheme="brand" 
          onClick={() => setIsAddModalOpen(true)}
        >
          Добавить товар
        </Button>
      </Flex>

      <InputGroup mb={4}>
        <InputLeftElement pointerEvents="none">
          <Icon as={FiSearch} color="gray.400" />
        </InputLeftElement>
        <Input
          placeholder="Поиск товара..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </InputGroup>

      <Card bg={cardBg} boxShadow="sm" borderRadius="lg" overflow="hidden">
        <Table variant="simple">
          <Thead>
            <Tr>
              <Th>Название</Th>
              <Th>Описание</Th>
              <Th>Статус</Th>
              <Th width="150px">Действия</Th>
            </Tr>
          </Thead>
          <Tbody>
            {filteredProducts.length > 0 ? (
              filteredProducts.map(product => (
                <Tr key={product._id}>
                  <Td fontWeight="medium">{product.name}</Td>
                  <Td>{product.description || '-'}</Td>
                  <Td>
                    <Badge colorScheme={product.active ? 'green' : 'red'}>
                      {product.active ? 'Активен' : 'Неактивен'}
                    </Badge>
                  </Td>
                  <Td>
                    <HStack spacing={2}>
                      <IconButton
                        icon={<FiEdit />}
                        aria-label="Редактировать"
                        size="sm"
                        colorScheme="blue"
                        variant="ghost"
                        onClick={() => {
                          setEditedProduct({ ...product });
                          setIsEditModalOpen(true);
                        }}
                      />
                      <IconButton
                        icon={<FiTrash2 />}
                        aria-label="Удалить"
                        size="sm"
                        colorScheme="red"
                        variant="ghost"
                        onClick={() => {
                          setSelectedProduct(product);
                          setIsDeleteDialogOpen(true);
                        }}
                      />
                    </HStack>
                  </Td>
                </Tr>
              ))
            ) : (
              <Tr>
                <Td colSpan={4} textAlign="center" py={4}>
                  {searchTerm ? 'Не найдено товаров по вашему запросу' : 'Нет доступных товаров'}
                </Td>
              </Tr>
            )}
          </Tbody>
        </Table>
      </Card>

      {/* Add Product Modal */}
      <Modal isOpen={isAddModalOpen} onClose={() => setIsAddModalOpen(false)}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Добавить товар</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <VStack spacing={4}>
              <FormControl isRequired>
                <FormLabel>Название товара</FormLabel>
                <Input 
                  placeholder="Введите название" 
                  value={newProduct.name}
                  onChange={e => setNewProduct({ ...newProduct, name: e.target.value })}
                />
              </FormControl>
              <FormControl>
                <FormLabel>Описание</FormLabel>
                <Textarea 
                  placeholder="Описание товара или услуги"
                  value={newProduct.description || ''}
                  onChange={e => setNewProduct({ ...newProduct, description: e.target.value })}
                />
              </FormControl>
              <FormControl>
                <FormLabel>Статус</FormLabel>
                <Select 
                  value={newProduct.active ? "true" : "false"}
                  onChange={e => setNewProduct({ ...newProduct, active: e.target.value === "true" })}
                >
                  <option value="true">Активен</option>
                  <option value="false">Неактивен</option>
                </Select>
              </FormControl>
            </VStack>
          </ModalBody>
          <ModalFooter>
            <Button variant="outline" mr={3} onClick={() => setIsAddModalOpen(false)}>
              Отмена
            </Button>
            <Button 
              colorScheme="brand" 
              onClick={handleAddProduct}
              isLoading={submitLoading}
              isDisabled={!newProduct.name.trim()}
            >
              Создать
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>

      {/* Edit Product Modal */}
      <Modal isOpen={isEditModalOpen} onClose={() => setIsEditModalOpen(false)}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Редактировать товар</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            {editedProduct && (
              <VStack spacing={4}>
                <FormControl isRequired>
                  <FormLabel>Название товара</FormLabel>
                  <Input 
                    placeholder="Введите название" 
                    value={editedProduct.name}
                    onChange={e => setEditedProduct({ ...editedProduct, name: e.target.value })}
                  />
                </FormControl>
                <FormControl>
                  <FormLabel>Описание</FormLabel>
                  <Textarea 
                    placeholder="Описание товара или услуги"
                    value={editedProduct.description || ''}
                    onChange={e => setEditedProduct({ ...editedProduct, description: e.target.value })}
                  />
                </FormControl>
                <FormControl>
                  <FormLabel>Статус</FormLabel>
                  <Select 
                    value={editedProduct.active ? "true" : "false"}
                    onChange={e => setEditedProduct({ ...editedProduct, active: e.target.value === "true" })}
                  >
                    <option value="true">Активен</option>
                    <option value="false">Неактивен</option>
                  </Select>
                </FormControl>
              </VStack>
            )}
          </ModalBody>
          <ModalFooter>
            <Button variant="outline" mr={3} onClick={() => setIsEditModalOpen(false)}>
              Отмена
            </Button>
            <Button 
              colorScheme="brand" 
              onClick={handleEditProduct}
              isLoading={submitLoading}
              isDisabled={!editedProduct?.name.trim()}
            >
              Сохранить
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>

      {/* Delete Confirmation Dialog */}
      <AlertDialog
        isOpen={isDeleteDialogOpen}
        leastDestructiveRef={undefined}
        onClose={() => setIsDeleteDialogOpen(false)}
      >
        <AlertDialogOverlay>
          <AlertDialogContent>
            <AlertDialogHeader fontSize="lg" fontWeight="bold">
              Удаление товара
            </AlertDialogHeader>

            <AlertDialogBody>
              Вы уверены, что хотите удалить "{selectedProduct?.name}"? Это действие нельзя отменить.
            </AlertDialogBody>

            <AlertDialogFooter>
              <Button onClick={() => setIsDeleteDialogOpen(false)}>
                Отмена
              </Button>
              <Button 
                colorScheme="red" 
                onClick={handleDeleteProduct} 
                ml={3}
                isLoading={submitLoading}
              >
                Удалить
              </Button>
            </AlertDialogFooter>
          </AlertDialogContent>
        </AlertDialogOverlay>
      </AlertDialog>
    </Box>
  );
};

// Orders Panel Component
const OrdersPanel = ({ isAdmin }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [isOrderDetailsOpen, setIsOrderDetailsOpen] = useState(false);
  const [isStatusUpdateLoading, setIsStatusUpdateLoading] = useState(false);
  const [dateRange, setDateRange] = useState({ start: '', end: '' });
  const toast = useToast();

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      const res = await API.get('/orders');
      setOrders(res.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching orders:', err);
      toast({
        title: 'Ошибка загрузки заказов',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
      setLoading(false);
    }
  };

  const updateOrderStatus = async (orderId, newStatus) => {
    try {
      setIsStatusUpdateLoading(true);
      await API.put(`/orders/${orderId}/status`, { status: newStatus });

      // Update order in the local state
      setOrders(orders.map(order => 
        order._id === orderId ? { ...order, status: newStatus } : order
      ));

      // If we're updating the selected order, update it too
      if (selectedOrder && selectedOrder._id === orderId) {
        setSelectedOrder({ ...selectedOrder, status: newStatus });
      }

      toast({
        title: 'Статус заказа обновлен',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (err) {
      console.error('Error updating order status:', err);
      toast({
        title: 'Ошибка обновления статуса',
        description: err.response?.data?.message || 'Что-то пошло не так',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setIsStatusUpdateLoading(false);
    }
  };

  const filteredOrders = orders
    .filter(order => statusFilter ? order.status === statusFilter : true)
    .filter(order => {
      // Search by order number or clinic name
      const matchesSearch = order.orderNumber.toString().includes(searchTerm) || 
        (order.clinic && order.clinic.name.toLowerCase().includes(searchTerm.toLowerCase()));

      // Filter by date range if provided
      let matchesDateRange = true;
      if (dateRange.start && dateRange.end) {
        const orderDate = new Date(order.createdAt);
        const startDate = new Date(dateRange.start);
        const endDate = new Date(dateRange.end);
        // Set end date to end of day for inclusive comparison
        endDate.setHours(23, 59, 59, 999);

        matchesDateRange = orderDate >= startDate && orderDate <= endDate;
      }

      return matchesSearch && matchesDateRange;
    })
    .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)); // Newest first

  const cardBg = useColorModeValue('white', 'gray.700');
  const borderColor = useColorModeValue('gray.200', 'gray.600');

  if (loading) {
    return (
      <Center h="200px">
        <Spinner size="xl" color="brand.500" thickness="4px" />
      </Center>
    );
  }

  return (
    <Box>
      <Flex justify="space-between" align="center" mb={4}>
        <Heading size="md">Управление заказами</Heading>
        {!isAdmin && (
          <Button 
            leftIcon={<FiPlus />} 
            colorScheme="brand"
            as={RouterLink}
            to="/orders/create"
          >
            Создать заказ
          </Button>
        )}
      </Flex>

      <Stack spacing={4} direction={{ base: "column", md: "row" }} mb={4}>
        <InputGroup>
          <InputLeftElement pointerEvents="none">
            <Icon as={FiSearch} color="gray.400" />
          </InputLeftElement>
          <Input
            placeholder="Поиск заказа..."
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value)}
          />
        </InputGroup>

        <Select 
          placeholder="Все статусы" 
          value={statusFilter} 
          onChange={e => setStatusFilter(e.target.value)}
          maxWidth={{ base: "100%", md: "200px" }}
        >
          <option value="pending">Не оплачен</option>
          <option value="paid">Оплачен</option>
          <option value="cancelled">Отменен</option>
        </Select>
      </Stack>

      <Stack spacing={4} direction={{ base: "column", md: "row" }} mb={4}>
        <FormControl>
          <FormLabel fontSize="sm">С даты</FormLabel>
          <Input 
            type="date" 
            value={dateRange.start}
            onChange={e => setDateRange({...dateRange, start: e.target.value})}
          />
        </FormControl>
        <FormControl>
          <FormLabel fontSize="sm">По дату</FormLabel>
          <Input 
            type="date" 
            value={dateRange.end}
            onChange={e => setDateRange({...dateRange, end: e.target.value})}
          />
        </FormControl>
        <FormControl display="flex" alignItems="flex-end">
          <Button 
            onClick={() => setDateRange({ start: '', end: '' })} 
            size="sm"
            variant="outline"
          >
            Сбросить
          </Button>
        </FormControl>
      </Stack>

      <Text fontSize="sm" mb={2}>
        Найдено заказов: {filteredOrders.length}
      </Text>

      <Card bg={cardBg} boxShadow="sm" borderRadius="lg" overflow="hidden">
        <Table variant="simple">
          <Thead>
            <Tr>
              <Th>№ заказа</Th>
              {isAdmin && <Th>Клиника</Th>}
              <Th>Дата</Th>
              <Th>Сумма</Th>
              <Th>Статус</Th>
              <Th width="120px">Действия</Th>
            </Tr>
          </Thead>
          <Tbody>
            {filteredOrders.length > 0 ? (
              filteredOrders.map(order => (
                <Tr key={order._id}>
                  <Td fontWeight="medium">#{order.orderNumber}</Td>
                  {isAdmin && <Td>{order.clinic?.name || '-'}</Td>}
                  <Td>{new Date(order.createdAt).toLocaleDateString()} {new Date(order.createdAt).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}</Td>
                  <Td fontWeight="medium">₽{order.totalAmount.toLocaleString()}</Td>
                  <Td>
                    <Badge colorScheme={getStatusColorScheme(order.status)}>
                      {getStatusText(order.status)}
                    </Badge>
                  </Td>
                  <Td>
                    <HStack spacing={2}>
                      {isAdmin && order.status === 'pending' && (
                        <IconButton
                          icon={<FiCheck />}
                          aria-label="Отметить как оплаченный"
                          size="sm"
                          colorScheme="green"
                          variant="ghost"
                          onClick={() => updateOrderStatus(order._id, 'paid')}
                          isLoading={isStatusUpdateLoading && selectedOrder?._id === order._id}
                        />
                      )}
                      <IconButton
                        icon={<FiCornerDownRight />}
                        aria-label="Детали"
                        size="sm"
                        colorScheme="blue"
                        variant="ghost"
                        onClick={() => {
                          setSelectedOrder(order);
                          setIsOrderDetailsOpen(true);
                        }}
                      />
                    </HStack>
                  </Td>
                </Tr>
              ))
            ) : (
              <Tr>
                <Td colSpan={isAdmin ? 6 : 5} textAlign="center" py={4}>
                  Заказы не найдены
                </Td>
              </Tr>
            )}
          </Tbody>
        </Table>
      </Card>

      {/* Order Details Modal */}
      <Modal 
        isOpen={isOrderDetailsOpen} 
        onClose={() => setIsOrderDetailsOpen(false)}
        size="xl"
      >
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>
            Заказ #{selectedOrder?.orderNumber}
            <Badge 
              ml={2} 
              colorScheme={selectedOrder && getStatusColorScheme(selectedOrder.status)}
            >
              {selectedOrder && getStatusText(selectedOrder.status)}
            </Badge>
          </ModalHeader>
          <ModalCloseButton />

          <ModalBody>
            {selectedOrder && (
              <Stack spacing={6}>
                <SimpleGrid columns={{ base: 1, md: 2 }} spacing={4}>
                  <Box>
                    <Text fontWeight="bold" mb={1}>Информация о заказе</Text>
                    <Stack spacing={1}>
                      <Flex justify="space-between">
                        <Text color="gray.500">Дата создания:</Text>
                        <Text>{new Date(selectedOrder.createdAt).toLocaleString()}</Text>
                      </Flex>
                      {selectedOrder.clinic && (
                        <Flex justify="space-between">
                          <Text color="gray.500">Клиника:</Text>
                          <Text>{selectedOrder.clinic.name}</Text>
                        </Flex>
                      )}
                      <Flex justify="space-between">
                        <Text color="gray.500">Общая сумма:</Text>
                        <Text fontWeight="bold">₽{selectedOrder.totalAmount.toLocaleString()}</Text>
                      </Flex>
                    </Stack>
                  </Box>

                  {isAdmin && (
                    <Box>
                      <Text fontWeight="bold" mb={1}>Управление заказом</Text>
                      <Stack spacing={2}>
                        <FormControl>
                          <FormLabel fontSize="sm">Изменить статус заказа</FormLabel>
                          <Select 
                            value={selectedOrder.status}
                            onChange={(e) => updateOrderStatus(selectedOrder._id, e.target.value)}
                            isDisabled={isStatusUpdateLoading}
                          >
                            <option value="pending">Не оплачен</option>
                            <option value="paid">Оплачен</option>
                            <option value="cancelled">Отменен</option>
                          </Select>
                        </FormControl>
                      </Stack>
                    </Box>
                  )}
                </SimpleGrid>

                <Divider />

                <Box>
                  <Text fontWeight="bold" mb={2}>Товары в заказе</Text>
                  <Table size="sm" variant="simple">
                    <Thead>
                      <Tr>
                        <Th>Наименование</Th>
                        <Th isNumeric>Кол-во</Th>
                        <Th isNumeric>Цена</Th>
                        <Th isNumeric>Сумма</Th>
                      </Tr>
                    </Thead>
                    <Tbody>
                      {selectedOrder.items.map((item, index) => (
                        <Tr key={index}>
                          <Td>{item.product?.name || 'Неизвестный товар'}</Td>
                          <Td isNumeric>{item.quantity}</Td>
                          <Td isNumeric>₽{item.price?.toLocaleString() || '-'}</Td>
                          <Td isNumeric fontWeight="medium">
                            ₽{(item.price * item.quantity).toLocaleString()}
                          </Td>
                        </Tr>
                      ))}
                    </Tbody>
                    <Tfoot>
                      <Tr>
                        <Th colSpan={3} textAlign="right">Итого:</Th>
                        <Th isNumeric fontSize="md">₽{selectedOrder.totalAmount.toLocaleString()}</Th>
                      </Tr>
                    </Tfoot>
                  </Table>
                </Box>

                {selectedOrder.notes && (
                  <>
                    <Divider />
                    <Box>
                      <Text fontWeight="bold" mb={1}>Примечания к заказу</Text>
                      <Text>{selectedOrder.notes}</Text>
                    </Box>
                  </>
                )}
              </Stack>
            )}
          </ModalBody>

          <ModalFooter>
            <Button onClick={() => setIsOrderDetailsOpen(false)}>
              Закрыть
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </Box>
  );
};

